import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
  Alert,
  TextInput,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector, useDispatch } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { RootState } from '../../store/store';
import { setAssignments, setLoading, setError } from '../../store/slices/workerSlice';
import WorkerHeader from '../../components/WorkerHeader';
import locationService from '../../services/locationService';
import timeTrackingService from '../../services/timeTrackingService';
import { apiService } from '../../services/apiService';

interface Assignment {
  id: string;
  issueId: string;
  workerId: string;
  status: 'assigned' | 'in_progress' | 'completed' | 'on_hold';
  priority: 'low' | 'medium' | 'high';
  assignedAt: string;
  estimatedDuration?: number;
  issue: {
    id: string;
    title: string;
    description: string;
    location: string;
    category: string;
    reportedAt: string;
  } | null;
}

const WorkerAssignmentsScreen = () => {
  const dispatch = useDispatch();
  const { user, token } = useSelector((state: RootState) => state.auth);
  const { assignments, loading } = useSelector((state: RootState) => state.worker);

  const [assignmentsList, setAssignmentsList] = useState<Assignment[]>([]);
  const [filteredAssignments, setFilteredAssignments] = useState<Assignment[]>([]);
  const [refreshing, setRefreshing] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedFilter, setSelectedFilter] = useState<'all' | 'assigned' | 'in_progress' | 'completed'>('all');

  const fetchAssignments = async () => {
    if (!user?.id) return;

    try {
      dispatch(setLoading(true));

      // Use the new API service method
      const response = await apiService.getWorkerAssignments(user.id);

      if (response.success && response.data) {
        // Transform the data to match the expected Assignment interface
        const transformedAssignments = response.data.assignments.map(assignment => ({
          id: assignment.id,
          issueId: assignment.issueId,
          workerId: assignment.workerId,
          status: assignment.status as 'assigned' | 'in_progress' | 'completed' | 'on_hold',
          priority: assignment.priority as 'low' | 'medium' | 'high',
          assignedAt: assignment.assignedAt,
          estimatedDuration: assignment.estimatedDuration,
          issue: assignment.issue ? {
            id: assignment.issue.id.toString(),
            title: assignment.issue.title,
            description: assignment.issue.description,
            location: assignment.issue.location,
            category: assignment.issue.category,
            reportedAt: assignment.issue.reportedAt,
          } : null
        }));

        setAssignmentsList(transformedAssignments);
        setFilteredAssignments(transformedAssignments);
        dispatch(setAssignments(transformedAssignments));
      } else {
        const errorMessage = response.error?.message || 'Failed to fetch assignments';
        dispatch(setError(errorMessage));
        Alert.alert('Error', errorMessage);
      }
    } catch (error) {
      console.error('Assignments fetch error:', error);
      dispatch(setError('Network error occurred'));
      Alert.alert('Error', 'Network error occurred');
    } finally {
      dispatch(setLoading(false));
      setRefreshing(false);
    }
  };

  const startWork = async (assignmentId: string) => {
    try {
      // Get current location for check-in
      const currentLocation = await locationService.getCurrentLocationWithRetry(3, 2000);

      if (!currentLocation) {
        Alert.alert(
          'Location Required',
          'Location access is required to start work. This helps verify you are at the work site.',
          [
            { text: 'Cancel', style: 'cancel' },
            { text: 'Retry', onPress: () => startWork(assignmentId) }
          ]
        );
        return;
      }

      // Check location accuracy
      if (!locationService.isLocationAccurate(currentLocation, 100)) {
        Alert.alert(
          'Location Accuracy',
          `Location accuracy is ${currentLocation.accuracy?.toFixed(0)}m. For better tracking, please move to an area with better GPS signal.`,
          [
            { text: 'Cancel', style: 'cancel' },
            { text: 'Continue Anyway', onPress: () => proceedWithStartWork(assignmentId, currentLocation) }
          ]
        );
        return;
      }

      await proceedWithStartWork(assignmentId, currentLocation);
    } catch (error) {
      console.error('Start work error:', error);
      Alert.alert('Error', 'Failed to start work. Please try again.');
    }
  };

  const proceedWithStartWork = async (assignmentId: string, location: any) => {
    try {
      const response = await fetch(`http://192.168.29.36:3003/api/v1/workers/assignments/${assignmentId}/start`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          location: locationService.createLocationPayload(location)
        }),
      });

      const data = await response.json();

      if (data.success) {
        // Start time tracking
        if (user?.id) {
          try {
            const assignment = assignmentsList.find(a => a.id === assignmentId);
            await timeTrackingService.startTimeTracking(
              assignmentId,
              assignment?.issue?.id || 'unknown',
              user.id,
              location
            );
            console.log('⏱️ Time tracking started for assignment:', assignmentId);
          } catch (timeError) {
            console.error('Error starting time tracking:', timeError);
          }
        }

        Alert.alert(
          'Work Started',
          `Work started successfully at ${locationService.formatLocationForDisplay(location)}\n\nTime tracking has begun.`,
          [{ text: 'OK' }]
        );
        fetchAssignments(); // Refresh the list
      } else {
        Alert.alert('Error', data.error?.message || 'Failed to start work');
      }
    } catch (error) {
      console.error('Start work API error:', error);
      Alert.alert('Error', 'Network error occurred');
    }
  };

  const completeWork = async (assignmentId: string) => {
    try {
      // Get current location for check-out
      const currentLocation = await locationService.getCurrentLocationWithRetry(3, 2000);

      if (!currentLocation) {
        Alert.alert(
          'Location Required',
          'Location access is required to complete work. This helps verify work completion at the site.',
          [
            { text: 'Cancel', style: 'cancel' },
            { text: 'Retry', onPress: () => completeWork(assignmentId) }
          ]
        );
        return;
      }

      await proceedWithCompleteWork(assignmentId, currentLocation);
    } catch (error) {
      console.error('Complete work error:', error);
      Alert.alert('Error', 'Failed to complete work. Please try again.');
    }
  };

  const proceedWithCompleteWork = async (assignmentId: string, location: any) => {
    try {
      const response = await fetch(`http://192.168.29.36:3003/api/v1/workers/assignments/${assignmentId}/complete`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          notes: 'Work completed successfully',
          photos: [],
          location: locationService.createLocationPayload(location)
        }),
      });

      const data = await response.json();

      if (data.success) {
        Alert.alert(
          'Work Completed',
          `Work completed successfully at ${locationService.formatLocationForDisplay(location)}`,
          [{ text: 'OK' }]
        );
        fetchAssignments(); // Refresh the list
      } else {
        Alert.alert('Error', data.error?.message || 'Failed to complete work');
      }
    } catch (error) {
      console.error('Complete work API error:', error);
      Alert.alert('Error', 'Network error occurred');
    }
  };

  useEffect(() => {
    fetchAssignments();
  }, [user?.id]);

  useEffect(() => {
    filterAssignments();
  }, [assignmentsList, searchQuery, selectedFilter]);

  const filterAssignments = () => {
    let filtered = assignmentsList;

    // Filter by status
    if (selectedFilter !== 'all') {
      filtered = filtered.filter(assignment => assignment.status === selectedFilter);
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(assignment =>
        assignment.issue?.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        assignment.issue?.location.toLowerCase().includes(searchQuery.toLowerCase()) ||
        assignment.issue?.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredAssignments(filtered);
  };

  const onRefresh = () => {
    setRefreshing(true);
    fetchAssignments();
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#f44336';
      case 'medium': return '#FF9800';
      case 'low': return '#4CAF50';
      default: return '#666';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'assigned': return '#2196F3';
      case 'in_progress': return '#FF9800';
      case 'completed': return '#4CAF50';
      case 'on_hold': return '#9C27B0';
      default: return '#666';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'assigned': return 'clipboard';
      case 'in_progress': return 'sync';
      case 'completed': return 'checkmark-circle';
      case 'on_hold': return 'pause-circle';
      default: return 'help-circle';
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const renderAssignmentCard = (assignment: Assignment) => (
    <View key={assignment.id} style={styles.assignmentCard}>
      <View style={styles.cardHeader}>
        <View style={styles.statusBadge}>
          <Ionicons 
            name={getStatusIcon(assignment.status) as any} 
            size={14} 
            color={getStatusColor(assignment.status)} 
          />
          <Text style={[styles.statusText, { color: getStatusColor(assignment.status) }]}>
            {assignment.status.replace('_', ' ').toUpperCase()}
          </Text>
        </View>
        <View style={[styles.priorityBadge, { backgroundColor: `${getPriorityColor(assignment.priority)}20` }]}>
          <Text style={[styles.priorityText, { color: getPriorityColor(assignment.priority) }]}>
            {assignment.priority.toUpperCase()}
          </Text>
        </View>
      </View>

      <Text style={styles.issueTitle}>{assignment.issue?.title || 'Issue Title'}</Text>
      <Text style={styles.issueDescription} numberOfLines={2}>
        {assignment.issue?.description || 'No description available'}
      </Text>

      <View style={styles.issueDetails}>
        <View style={styles.detailRow}>
          <Ionicons name="location" size={14} color="#666" />
          <Text style={styles.detailText}>{assignment.issue?.location || 'Location'}</Text>
        </View>
        <View style={styles.detailRow}>
          <Ionicons name="folder" size={14} color="#666" />
          <Text style={styles.detailText}>{assignment.issue?.category || 'Category'}</Text>
        </View>
        <View style={styles.detailRow}>
          <Ionicons name="time" size={14} color="#666" />
          <Text style={styles.detailText}>Assigned: {formatDate(assignment.assignedAt)}</Text>
        </View>
        {assignment.estimatedDuration && (
          <View style={styles.detailRow}>
            <Ionicons name="hourglass" size={14} color="#666" />
            <Text style={styles.detailText}>Est. Duration: {assignment.estimatedDuration}h</Text>
          </View>
        )}
      </View>

      <View style={styles.cardActions}>
        {assignment.status === 'assigned' && (
          <TouchableOpacity 
            style={[styles.actionButton, styles.startButton]}
            onPress={() => startWork(assignment.id)}
          >
            <Ionicons name="play" size={16} color="white" />
            <Text style={styles.actionButtonText}>Start Work</Text>
          </TouchableOpacity>
        )}

        {assignment.status === 'in_progress' && (
          <>
            <TouchableOpacity 
              style={[styles.actionButton, styles.progressButton]}
              onPress={() => {/* Navigate to progress screen */}}
            >
              <Ionicons name="camera" size={16} color="white" />
              <Text style={styles.actionButtonText}>Update Progress</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.actionButton, styles.completeButton]}
              onPress={() => completeWork(assignment.id)}
            >
              <Ionicons name="checkmark" size={16} color="white" />
              <Text style={styles.actionButtonText}>Complete</Text>
            </TouchableOpacity>
          </>
        )}

        <TouchableOpacity 
          style={[styles.actionButton, styles.detailsButton]}
          onPress={() => {/* Navigate to issue details */}}
        >
          <Ionicons name="eye" size={16} color="#FF6B35" />
          <Text style={[styles.actionButtonText, { color: '#FF6B35' }]}>View Details</Text>
        </TouchableOpacity>
      </View>
    </View>
  );

  const insets = useSafeAreaInsets();

  return (
    <View style={styles.container}>
      <WorkerHeader
        title="My Assignments"
        subtitle={`${filteredAssignments.length} assignments`}
      />

      {/* Search and Filter */}
      <View style={styles.searchContainer}>
        <View style={styles.searchInputContainer}>
          <Ionicons name="search" size={20} color="#666" />
          <TextInput
            style={styles.searchInput}
            placeholder="Search assignments..."
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>
      </View>

      <View style={styles.filterContainer}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {['all', 'assigned', 'in_progress', 'completed'].map((filter) => (
            <TouchableOpacity
              key={filter}
              style={[
                styles.filterButton,
                selectedFilter === filter && styles.activeFilterButton
              ]}
              onPress={() => setSelectedFilter(filter as any)}
            >
              <Text style={[
                styles.filterButtonText,
                selectedFilter === filter && styles.activeFilterButtonText
              ]}>
                {filter === 'all' ? 'All' : filter.replace('_', ' ')}
              </Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>

      {/* Assignments List */}
      <ScrollView 
        style={styles.scrollView}
        contentContainerStyle={[styles.scrollContent, { paddingBottom: insets.bottom + 80 }]}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        showsVerticalScrollIndicator={false}
      >
        {filteredAssignments.length > 0 ? (
          filteredAssignments.map(renderAssignmentCard)
        ) : (
          <View style={styles.emptyState}>
            <Ionicons name="clipboard-outline" size={64} color="#ccc" />
            <Text style={styles.emptyStateText}>No assignments found</Text>
            <Text style={styles.emptyStateSubtext}>
              {searchQuery || selectedFilter !== 'all' 
                ? 'Try adjusting your search or filter' 
                : 'You have no assignments at the moment'}
            </Text>
          </View>
        )}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  scrollContent: {
    paddingTop: 8,
  },
  searchContainer: {
    paddingHorizontal: 16,
    paddingVertical: 12,
    backgroundColor: 'white',
  },
  searchInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
    borderRadius: 8,
    paddingHorizontal: 12,
    paddingVertical: 8,
  },
  searchInput: {
    flex: 1,
    marginLeft: 8,
    fontSize: 16,
    color: '#333',
  },
  filterContainer: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    backgroundColor: 'white',
    borderBottomWidth: 1,
    borderBottomColor: '#e0e0e0',
  },
  filterButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
    backgroundColor: '#f5f5f5',
    marginRight: 8,
  },
  activeFilterButton: {
    backgroundColor: '#FF6B35',
  },
  filterButtonText: {
    fontSize: 14,
    color: '#666',
    fontWeight: '500',
    textTransform: 'capitalize',
  },
  activeFilterButtonText: {
    color: 'white',
  },
  scrollView: {
    flex: 1,
    paddingHorizontal: 16,
  },
  assignmentCard: {
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 16,
    marginVertical: 8,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.1,
    shadowRadius: 2,
  },
  cardHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
  },
  statusBadge: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 12,
    backgroundColor: '#f0f0f0',
  },
  statusText: {
    fontSize: 10,
    fontWeight: '600',
    marginLeft: 4,
  },
  priorityBadge: {
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 12,
  },
  priorityText: {
    fontSize: 10,
