import React, { useEffect, useState, useCallback } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
  Alert,
  StatusBar,
  Platform,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector, useDispatch } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { RootState } from '../../store/store';
import { setWorkerStats, setAssignments, setLoading, setError } from '../../store/slices/workerSlice';
import { logout } from '../../store/slices/authSlice';
import OfflineIndicator from '../../components/OfflineIndicator';
import { offlineService } from '../../services/offlineService';
import { apiService } from '../../services/apiService';
import { tokenStorage } from '../../utils/tokenStorage';
import { authDebug } from '../../utils/authDebug';
import IssueDetailsModal from '../../components/IssueDetailsModal';

interface DashboardStats {
  totalAssigned: number;
  todayAssigned: number;
  inProgress: number;
  completed: number;
  pending: number;
}

interface PriorityIssue {
  id: string;
  issueId: string;
  priority: 'high' | 'medium' | 'low';
  issue: {
    title: string;
    location: string;
    category: string;
  };
}

const WorkerDashboardScreen = () => {
  const dispatch = useDispatch();
  const { user, token } = useSelector((state: RootState) => state.auth);
  const { stats, loading } = useSelector((state: RootState) => state.worker);

  const [dashboardStats, setDashboardStats] = useState<DashboardStats>({
    totalAssigned: 0,
    todayAssigned: 0,
    inProgress: 0,
    completed: 0,
    pending: 0,
  });
  const [priorityIssues, setPriorityIssues] = useState<PriorityIssue[]>([]);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedIssue, setSelectedIssue] = useState<any>(null);
  const [issueDetailsVisible, setIssueDetailsVisible] = useState(false);

  const fetchDashboardData = async () => {
    if (!user?.id) {
      console.log('❌ No user ID available');
      return;
    }

    // Check if we have a fallback token in Redux state
    if (token && token.startsWith('fallback_token')) {
      console.log('❌ Fallback token detected in Redux state:', token.substring(0, 30) + '...');

      // Immediately clear Redux state and force logout
      console.log('🔄 Clearing Redux state immediately...');
      dispatch(logout());

      Alert.alert(
        'Authentication Error',
        'Invalid authentication detected. Please login again.',
        [
          {
            text: 'Login',
            onPress: async () => {
              try {
                await authDebug.completeAuthReset();
              } catch (error) {
                console.error('Error during auth reset:', error);
              }
            }
          }
        ]
      );
      return;
    }

    if (!token) {
      console.log('❌ No authentication token available');

      // Debug current auth state
      await authDebug.debugAuthState();

      Alert.alert(
        'Authentication Required',
        'No valid authentication token found. We will reset your authentication completely.',
        [
          {
            text: 'Reset & Login',
            onPress: async () => {
              try {
                await authDebug.completeAuthReset();
                // The app should automatically redirect to login screen
              } catch (error) {
                console.error('Error during complete auth reset:', error);
              }
            }
          }
        ]
      );
      return;
    }

    try {
      dispatch(setLoading(true));

      // Use the new API service method
      const response = await apiService.getWorkerDashboard(user.id);

      if (response.success && response.data) {
        setDashboardStats(response.data.stats);
        setPriorityIssues(response.data.priorityIssues || []);

        // Update Redux store
        dispatch(setWorkerStats({
          totalAssigned: response.data.stats.totalAssigned,
          inProgress: response.data.stats.inProgress,
          completed: response.data.stats.completed,
          avgResponseTime: 0, // Will be calculated from actual data
          completionRate: response.data.stats.totalAssigned > 0 
            ? (response.data.stats.completed / response.data.stats.totalAssigned) * 100 
            : 0,
          rating: response.data.worker.rating || 0,
        }));

        // Update user data with worker info
        if (response.data.worker) {
          console.log('✅ Worker data loaded:', response.data.worker.name);
        }
      } else {
        const errorMessage = response.error?.message || 'Failed to fetch dashboard data';
        console.error('❌ Dashboard API error:', response.error);

        // Handle authentication errors specifically
        if (response.error?.code === 'INVALID_TOKEN' || response.error?.code === 'MALFORMED_TOKEN' || response.error?.code === 'NO_TOKEN') {
          console.log('🔍 Authentication error detected, debugging...');
          await authDebug.debugAuthState();

          Alert.alert(
            'Authentication Error',
            'Your session is invalid. We will completely reset your authentication.',
            [
              {
                text: 'Reset & Login',
                onPress: async () => {
                  try {
                    await authDebug.completeAuthReset();
                    // The app should automatically redirect to login screen
                  } catch (error) {
                    console.error('Error during complete auth reset:', error);
                  }
                }
              }
            ]
          );
        } else {
          dispatch(setError(errorMessage));
          Alert.alert('Error', errorMessage);
        }
      }
    } catch (error) {
      console.error('❌ Dashboard fetch error:', error);
      dispatch(setError('Network error occurred'));
      Alert.alert('Error', 'Network error occurred');
    } finally {
      dispatch(setLoading(false));
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, [user?.id]);

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchDashboardData().finally(() => setRefreshing(false));
  }, []);

  const handleViewIssueDetails = async (issueId: string) => {
    try {
      console.log('📋 WorkerDashboard: Fetching issue details for:', issueId);
      console.log('📋 WorkerDashboard: About to call apiService.getIssueDetails');

      // Call API to get full issue details
      const response = await apiService.getIssueDetails(issueId);

      console.log('📋 WorkerDashboard: API response received:', response);

      if (response.success && response.data) {
        // Transform the data to match our modal interface
        const issue = response.data as any; // Type assertion for flexibility
        const issueData = {
          id: issue.id,
          title: issue.title || 'No Title',
          description: issue.description || 'No Description',
          category: issue.category || 'Unknown',
          priority: issue.priority || 'MEDIUM',
          status: issue.status || 'REPORTED',
          location: {
            latitude: issue.location?.latitude || 0,
            longitude: issue.location?.longitude || 0,
            address: issue.location?.address || 'Location not available',
          },
          media: issue.attachments || issue.media || [],
          reportedBy: {
            name: issue.reporterName || 'Unknown',
            email: issue.reporterEmail || '',
            phone: issue.reporterPhone || '',
          },
          reportedAt: issue.createdAt || new Date().toISOString(),
          assignedAt: issue.assignedAt || issue.updatedAt,
          estimatedResolution: issue.estimatedResolution,
        };

        setSelectedIssue(issueData);
        setIssueDetailsVisible(true);
      } else {
        Alert.alert('Error', 'Failed to fetch issue details');
      }
    } catch (error) {
      console.error('Error fetching issue details:', error);
      Alert.alert('Error', 'Failed to fetch issue details');
    }
  };

  const handleStartWork = async (issueId: string) => {
    try {
      console.log('🚀 Starting work on issue:', issueId);

      // TODO: Implement proper API call to start work
      // For now, just show success message
      Alert.alert('Success', 'Work started successfully! (Feature will be implemented soon)');
      setIssueDetailsVisible(false);
      // Refresh dashboard data
      fetchDashboardData();
    } catch (error) {
      console.error('Error starting work:', error);
      Alert.alert('Error', 'Failed to start work on issue');
    }
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good Morning';
    if (hour < 17) return 'Good Afternoon';
    return 'Good Evening';
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#f44336';
      case 'medium': return '#FF9800';
      case 'low': return '#4CAF50';
      default: return '#666';
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'high': return 'alert-circle';
      case 'medium': return 'warning';
      case 'low': return 'information-circle';
      default: return 'help-circle';
    }
  };

  const insets = useSafeAreaInsets();

  const renderCustomHeader = () => {
    const statusBarHeight = Platform.OS === 'ios' ? insets.top : StatusBar.currentHeight || 0;

    return (
      <View style={[styles.customHeader, { paddingTop: statusBarHeight }]}>
        <View style={styles.headerContent}>
          {/* Left side - Greeting and basic info */}
          <View style={styles.headerLeft}>
            <Text style={styles.greeting}>{getGreeting()}</Text>
            <Text style={styles.userName}>{user?.name || 'Worker'}</Text>
            <View style={styles.workerInfo}>
              <Text style={styles.workerId}>ID: {user?.workerId || 'N/A'}</Text>
              <View style={styles.dot} />
              <Text style={styles.department}>{user?.department || 'Department'}</Text>
            </View>
          </View>

          {/* Right side - Rating and profile */}
          <View style={styles.headerRight}>
            <View style={styles.ratingBadge}>
              <Ionicons name="star" size={14} color="#FFD700" />
              <Text style={styles.ratingText}>{user?.rating || '0.0'}</Text>
            </View>
            <TouchableOpacity style={styles.profileButton}>
              <View style={styles.profileAvatar}>
                <Text style={styles.avatarText}>
                  {(user?.name || 'W').charAt(0).toUpperCase()}
                </Text>
              </View>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      {renderCustomHeader()}

      <ScrollView 
        style={styles.scrollView}
        contentContainerStyle={[styles.scrollContent, { paddingBottom: insets.bottom + 80 }]}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        showsVerticalScrollIndicator={false}
      >

        {/* Today's Summary */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Today's Summary</Text>
          <View style={styles.statsGrid}>
            <View style={[styles.statCard, styles.assignedCard]}>
              <Ionicons name="clipboard" size={24} color="#2196F3" />
              <Text style={styles.statNumber}>{dashboardStats.todayAssigned}</Text>
              <Text style={styles.statLabel}>Assigned Today</Text>
            </View>
            <View style={[styles.statCard, styles.progressCard]}>
              <Ionicons name="sync" size={24} color="#FF9800" />
              <Text style={styles.statNumber}>{dashboardStats.inProgress}</Text>
              <Text style={styles.statLabel}>In Progress</Text>
            </View>
            <View style={[styles.statCard, styles.completedCard]}>
              <Ionicons name="checkmark-circle" size={24} color="#4CAF50" />
              <Text style={styles.statNumber}>{dashboardStats.completed}</Text>
              <Text style={styles.statLabel}>Completed</Text>
            </View>
            <View style={[styles.statCard, styles.pendingCard]}>
              <Ionicons name="time" size={24} color="#9C27B0" />
              <Text style={styles.statNumber}>{dashboardStats.pending}</Text>
              <Text style={styles.statLabel}>Pending</Text>
            </View>
          </View>
        </View>

        {/* Priority Issues */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Priority Issues</Text>
            <TouchableOpacity>
              <Text style={styles.viewAllText}>View All</Text>
            </TouchableOpacity>
          </View>

          {priorityIssues.length > 0 ? (
            priorityIssues.map((item) => (
              <TouchableOpacity key={item.id} style={styles.issueCard}>
                <View style={styles.issueHeader}>
                  <View style={styles.priorityBadge}>
                    <Ionicons 
                      name={getPriorityIcon(item.priority) as any} 
                      size={14} 
                      color={getPriorityColor(item.priority)} 
                    />
                    <Text style={[styles.priorityText, { color: getPriorityColor(item.priority) }]}>
                      {item.priority.toUpperCase()}
                    </Text>
                  </View>
                </View>
                <Text style={styles.issueTitle}>{item.issue?.title || 'Issue Title'}</Text>
                <View style={styles.issueDetails}>
                  <View style={styles.issueDetailRow}>
                    <Ionicons name="location" size={14} color="#666" />
                    <Text style={styles.issueDetailText}>{item.issue?.location || 'Location'}</Text>
                  </View>
                  <View style={styles.issueDetailRow}>
                    <Ionicons name="folder" size={14} color="#666" />
                    <Text style={styles.issueDetailText}>{item.issue?.category || 'Category'}</Text>
                  </View>
                </View>
                <View style={styles.issueActions}>
                  <TouchableOpacity 
                    style={styles.actionButton}
                    onPress={() => handleViewIssueDetails(item.id)}
                  >
                    <Text style={styles.actionButtonText}>View Details</Text>
                  </TouchableOpacity>
                </View>
              </TouchableOpacity>
            ))
          ) : (
            <View style={styles.emptyState}>
              <Ionicons name="checkmark-circle-outline" size={48} color="#ccc" />
              <Text style={styles.emptyStateText}>No priority issues at the moment</Text>
              <Text style={styles.emptyStateSubtext}>Great job keeping up with your work!</Text>
            </View>
          )}
        </View>

        {/* Quick Actions */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Quick Actions</Text>
          <View style={styles.quickActions}>
            <TouchableOpacity style={styles.quickActionCard}>
              <Ionicons name="list" size={24} color="#FF6B35" />
              <Text style={styles.quickActionText}>My Assignments</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.quickActionCard}>
              <Ionicons name="map" size={24} color="#FF6B35" />
              <Text style={styles.quickActionText}>Map View</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.quickActionCard}>
              <Ionicons name="document-text" size={24} color="#FF6B35" />
              <Text style={styles.quickActionText}>Reports</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.quickActionCard}>
              <Ionicons name="person" size={24} color="#FF6B35" />
              <Text style={styles.quickActionText}>Profile</Text>
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
      <OfflineIndicator />

      {/* Issue Details Modal */}
      <IssueDetailsModal
        visible={issueDetailsVisible}
        issue={selectedIssue}
        onClose={() => setIssueDetailsVisible(false)}
        onStartWork={handleStartWork}
      />
    </View>
  );
};

