import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Alert,
  KeyboardAvoidingView,
  Platform,
  Image,
  ScrollView,
  Dimensions,
  Modal,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { useDispatch } from 'react-redux';
import { useNavigation } from '@react-navigation/native';
import { useTranslation } from 'react-i18next';
import { loginSuccess } from '../../store/slices/authSlice';
import { tokenStorage } from '../../utils/tokenStorage';
import { apiService, saveAuthToken } from '../../services/apiService';
import { signInWithGoogle } from '../../services/firebaseAuth';
import { ENABLE_MOCK_AUTH, isMockUser } from '../../config/mockAuth';

const LoginScreen = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [termsModal, setTermsModal] = useState(false);

  const dispatch = useDispatch();
  const navigation = useNavigation();

  const { t } = useTranslation();

  // Initialize Google Auth hook
  // No hooks needed for pure Firebase Google auth

  // Get screen dimensions for responsive design
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');
  const isTablet = screenWidth >= 768;
  const isSmallPhone = screenWidth < 350;
  const isLandscape = screenWidth > screenHeight;

  const handleLogin = async () => {
    if (!email || !password) {
      Alert.alert(t('common.error'), t('auth.fillAllFields'));
      return;
    }

    setLoading(true);

    try {
      // Use database authentication only (no Firebase, no mock auth)
      console.log('ðŸ” Attempting database login for:', email);
      const loginRes = await apiService.mockLogin(email, password); // This actually calls /mock-auth/login which is database auth

      if (!loginRes.success || !loginRes.data) {
        setLoading(false);
        Alert.alert(
          t('common.error'), 
          'Database login failed. Check your credentials.'
        );
        return;
      }

      await saveAuthToken(loginRes.data.token);

      const userData = {
        id: loginRes.data.user.id,
        name: loginRes.data.user.name || loginRes.data.user.fullName || '',
        email: loginRes.data.user.email,
        phone: loginRes.data.user.phone || loginRes.data.user.phoneNumber || '',
        role: (() => {
          const userRole = String(loginRes.data.user.role).toLowerCase();
          if (userRole === 'admin') return 'admin' as const;
          if (userRole === 'ground_worker' || userRole === 'department_head') return 'worker' as const;
          return 'citizen' as const;
        })(),
      };
      const token = loginRes.data.token;

      // Store token and user data
      await tokenStorage.storeAuthData(token, userData);

      dispatch(loginSuccess({
        user: userData,
        token: token,
      }));

      Alert.alert(
        'Success', 
        `Logged in successfully as ${loginRes.data.user.role}`
      );

    } catch (error) {
      console.error('Login error:', error);

      // Final fallback for user123
      if ((email === 'user123' || email === 'user123@example.com') && password === 'user') {
        const userData = {
          id: 'user_001',
          name: 'Test User',
          email: 'user123@example.com',
          phone: '+91-9876543210',
          role: 'citizen' as const,
        };
        const token = 'mock-jwt-token-user123';

        // Store token and user data
        await tokenStorage.storeAuthData(token, userData);

        dispatch(loginSuccess({
          user: userData,
          token: token,
        }));
        Alert.alert('Success', 'Logged in with offline mode');
      } else {
        Alert.alert('Error', 'Unable to connect to server and invalid credentials for offline mode.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      setLoading(true);
      console.log('[LoginScreen] Starting Firebase Google Sign-In...');

      // Use pure Firebase Google Sign-In
      const user = await signInWithGoogle();
      if (!user) return;

      console.log('[LoginScreen] Google Sign-In successful, getting ID token...');
      const idToken = await user.getIdToken();
      const res = await apiService.loginWithGoogle(idToken);

      if (res.success && res.data) {
        await saveAuthToken(res.data.token);
        const userData = {
          id: res.data.user.id,
          name: res.data.user.fullName || res.data.user.name || '',
          email: res.data.user.email,
          phone: res.data.user.phoneNumber || res.data.user.phone || '',
          role: (res.data.user.role === 'admin' || res.data.user.role === 'ADMIN') ? 'admin' as const : 'citizen' as const,
        };
        await tokenStorage.storeAuthData(res.data.token, userData);
        dispatch(loginSuccess({ user: userData, token: res.data.token }));
        Alert.alert('Success', 'Logged in with Google successfully');
      }
    } catch (error: any) {
      console.error('Google login error:', error);
      Alert.alert('Error', error.message || 'Google login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleForgotPassword = () => {
    navigation.navigate('ForgotPassword' as never);
  };

  const handleTermsPress = () => {
    setTermsModal(true);
  };

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView 
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.keyboardView}
        keyboardVerticalOffset={Platform.OS === 'ios' ? 0 : 20}
      >
        <ScrollView 
          contentContainerStyle={[styles.scrollContent, {
            paddingHorizontal: isTablet ? screenWidth * 0.15 : isSmallPhone ? 15 : 20,
            paddingTop: isLandscape ? 10 : 20,
            paddingBottom: isLandscape ? 10 : 20,
          }]}
          keyboardShouldPersistTaps="handled"
          showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
          contentInsetAdjustmentBehavior="never"
        >
          {/* Header */}
          <View style={styles.header} accessible={true} accessibilityRole="header">
            <Image 
              source={require('../../../assets/Jharkhand_Rajakiya_Chihna.png')} 
              style={styles.logo}
              resizeMode="contain"
              accessible={true}
              accessibilityLabel="Government of Jharkhand official emblem"
            />
            <Text 
              style={[styles.title, {
                fontSize: isTablet ? 28 : isSmallPhone ? 20 : 24,
              }]}
              accessible={true}
              accessibilityRole="header"
              accessibilityLabel="Civic Issue Reporter - Main application title"
            >
              Civic Issue Reporter
            </Text>
            <Text 
              style={[styles.subtitle, {
                fontSize: isTablet ? 18 : isSmallPhone ? 14 : 16,
              }]}
              accessible={true}
              accessibilityLabel="Government of Jharkhand"
            >
              Government of Jharkhand
            </Text>
          </View>

          {/* Login Form */}
          <View style={[styles.form, {
            maxWidth: isTablet ? 400 : '100%',
            alignSelf: 'center',
            padding: isSmallPhone ? 15 : 20,
            marginHorizontal: isTablet ? 0 : 5,
          }]}>
            <Text 
              style={[styles.formTitle, {
                fontSize: isTablet ? 22 : isSmallPhone ? 18 : 20,
              }]}
              accessible={true}
              accessibilityRole="header"
              accessibilityLabel="Welcome back - Login form"
            >
              {t('auth.welcome')}
            </Text>

            <View style={styles.inputContainer}>
              <Ionicons 
                name="mail-outline" 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={t('auth.email')}
                value={email}
                onChangeText={setEmail}
                keyboardType="email-address"
                autoCapitalize="none"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel="Email address input field"
                accessibilityHint="Enter your email address to login"
                returnKeyType="next"
                blurOnSubmit={false}
                onSubmitEditing={() => {
                  // Focus next input (password)
                }}
              />
            </View>

            <View style={styles.inputContainer}>
              <Ionicons 
                name="lock-closed-outline" 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={t('auth.password')}
                value={password}
                onChangeText={setPassword}
                secureTextEntry={!showPassword}
                autoCapitalize="none"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel="Password input field"
              />
              <TouchableOpacity 
                onPress={() => setShowPassword(!showPassword)} 
                style={styles.eyeIcon}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel={showPassword ? "Hide password" : "Show password"}
                accessibilityHint={showPassword ? "Tap to hide your password" : "Tap to show your password"}
              >
                <Ionicons 
                  name={showPassword ? "eye-off-outline" : "eye-outline"} 
                  size={20} 
                  color="#555" 
                />
              </TouchableOpacity>
            </View>

            <TouchableOpacity 
              onPress={handleForgotPassword}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel="Forgot password"
              accessibilityHint="Tap to reset your password"
            >
              <Text style={styles.forgotPassword}>{t('auth.forgotPassword')}</Text>
            </TouchableOpacity>

            <TouchableOpacity 
              style={[styles.loginButton, loading && styles.loginButtonDisabled]}
              onPress={handleLogin}
              disabled={loading}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel={loading ? "Logging in, please wait" : "Login button"}
              accessibilityHint="Tap to login with your email and password"
              accessibilityState={{ disabled: loading }}
            >
              <Text style={styles.loginButtonText}>
                {loading ? t('common.loading') : t('auth.loginButton')}
              </Text>
            </TouchableOpacity>

            {/* OAuth Buttons */}
            <View style={styles.divider}>
              <View style={styles.dividerLine} />
              <Text style={styles.dividerText}>{t('common.or')}</Text>
              <View style={styles.dividerLine} />
            </View>

            <TouchableOpacity 
              style={styles.googleButton} 
              onPress={handleGoogleLogin}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel="Continue with Google"
              accessibilityHint="Tap to login using your Google account"
            >
              <Ionicons 
                name="logo-google" 
                size={20} 
                color="#DB4437"
                accessible={false}
              />
              <Text style={styles.googleButtonText}>{t('auth.continueWithGoogle')}</Text>
            </TouchableOpacity>

            {/* Terms & Conditions */}
            <View 
              style={styles.termsContainer}
              accessible={true}
              accessibilityRole="text"
              accessibilityLabel="By logging in, you agree to our Terms and Conditions"
            >
              <Text style={styles.termsText}>By logging in, you agree to our </Text>
              <TouchableOpacity 
                onPress={handleTermsPress}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Terms and Conditions"
                accessibilityHint="Tap to view terms and conditions"
              >
                <Text style={styles.termsLink}>Terms & Conditions</Text>
              </TouchableOpacity>
              <Text style={styles.termsText}>.</Text>
            </View>

            {/* Register Link */}
            <View 
              style={styles.registerContainer}
              accessible={true}
              accessibilityRole="text"
              accessibilityLabel="Don't have an account? Register here"
            >
              <Text style={styles.registerText}>{t('auth.noAccount')} </Text>
              <TouchableOpacity 
                onPress={() => navigation.navigate('Register' as never)}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Register for new account"
                accessibilityHint="Tap to create a new account"
              >
                <Text style={styles.registerLink}>{t('auth.register')}</Text>
              </TouchableOpacity>
            </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>

    {/* Terms of Service Modal */}
    <Modal
      visible={termsModal}
      animationType="slide"
      presentationStyle="pageSheet"
    >
      <SafeAreaView style={styles.modalContainer}>
        <View style={styles.modalHeader}>
          <TouchableOpacity onPress={() => setTermsModal(false)}>
            <Ionicons name="close" size={24} color="#666" />
          </TouchableOpacity>
          <Text style={styles.modalTitle}>Terms of Service</Text>
          <View style={{ width: 24 }} />
        </View>
        
        <ScrollView style={styles.modalContent}>
          <Text style={styles.policyText}>
            <Text style={styles.policyTitle}>Terms of Service{'\n\n'}</Text>
            
            <Text style={styles.policySubtitle}>Acceptance of Terms{'\n'}</Text>
            By using this application, you agree to comply with these terms of service and all applicable laws and regulations.{'\n\n'}
            
            <Text style={styles.policySubtitle}>Proper Use{'\n'}</Text>
            This application is intended for reporting legitimate civic issues. Misuse, including false reports or spam, may result in account suspension.{'\n\n'}
            
            <Text style={styles.policySubtitle}>Content Guidelines{'\n'}</Text>
            All submitted content must be respectful, accurate, and relevant to civic issues. Inappropriate content will be removed.{'\n\n'}
            
            <Text style={styles.policySubtitle}>Government Services{'\n'}</Text>
            This app facilitates communication with government services but does not guarantee specific response times or outcomes.{'\n\n'}
            
            <Text style={styles.policySubtitle}>Modifications{'\n'}</Text>
            We reserve the right to modify these terms at any time. Continued use constitutes acceptance of updated terms.
          </Text>
        </ScrollView>
      </SafeAreaView>
    </Modal>
  </SafeAreaView>
);
};

