import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  Alert,
  Switch,
  Modal,
  TextInput,
  Linking,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import * as ImagePicker from 'expo-image-picker';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigation } from '@react-navigation/native';
import { useTranslation } from 'react-i18next';
import i18n from '../../i18n';
import { RootState } from '../../store/store';
import { logout, updateProfile } from '../../store/slices/authSlice';
import { updatePreferences } from '../../store/slices/userSlice';
import { tokenStorage } from '../../utils/tokenStorage';

const ProfileScreen = ({ route }: { route?: any }) => {
  const dispatch = useDispatch();
  const navigation = useNavigation();
  const user = useSelector((state: RootState) => state.auth.user);
  const preferences = useSelector((state: RootState) => state.user.preferences);

  const [notifications, setNotifications] = useState(true);

  // Modal states
  const [editProfileModal, setEditProfileModal] = useState(false);
  const [helpModal, setHelpModal] = useState(false);
  const [privacyModal, setPrivacyModal] = useState(false);
  const [termsModal, setTermsModal] = useState(false);
  const [aboutModal, setAboutModal] = useState(false);

  // Check if we should open Terms modal from navigation params
  useEffect(() => {
    if (route?.params?.openTerms) {
      setTermsModal(true);
    }
  }, [route?.params?.openTerms]);

  // Edit profile form states
  const [editForm, setEditForm] = useState({
    name: user?.name || '',
    email: user?.email || '',
    phone: user?.phone || '',
    address: user?.address || '',
  });

  const [profileImage, setProfileImage] = useState<string | null>(null);

  const handleLogout = () => {
    Alert.alert(
      'Logout',
      'Are you sure you want to logout?',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Logout',
          style: 'destructive',
          onPress: async () => {
            try {
              // Clear stored auth data
              await tokenStorage.clearAuthData();
              // Dispatch logout action
              dispatch(logout());
            } catch (error) {
              console.error('Error during logout:', error);
              // Still logout even if clearing storage fails
              dispatch(logout());
            }
          },
        },
      ]
    );
  };

  const handleLanguageChange = () => {
    console.log('Language change clicked'); // Debug log
    Alert.alert(
      'Select Language',
      'Choose your preferred language',
      [
        { 
          text: 'English', 
          onPress: () => {
            console.log('English selected');
            updateLanguage('en');
          }
        },
        { 
          text: 'हिंदी', 
          onPress: () => {
            console.log('Hindi selected');
            updateLanguage('hi');
          }
        },
        { 
          text: 'বাংলা', 
          onPress: () => {
            console.log('Bengali selected');
            updateLanguage('bn');
          }
        },
        { text: 'Cancel', style: 'cancel' },
      ],
      { cancelable: true }
    );
  };

  const updateLanguage = async (language: 'en' | 'hi' | 'bn') => {
    try {
      await i18n.changeLanguage(language);
      dispatch(updatePreferences({ language }));
      Alert.alert(i18n.t('common.success'), i18n.t('profile.languageChanged'));
    } catch (error) {
      console.error('Error changing language:', error);
      Alert.alert(i18n.t('common.error'), i18n.t('profile.languageChangeError'));
    }
  };



  // Profile image picker
  const pickImage = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });

    if (!result.canceled) {
      setProfileImage(result.assets[0].uri);
    }
  };

  // Edit profile handlers
  const handleEditProfile = () => {
    setEditProfileModal(true);
  };

  const saveProfile = () => {
    // Update Redux store with new profile data
    dispatch(updateProfile({
      name: editForm.name,
      email: editForm.email,
      phone: editForm.phone,
      address: editForm.address,
    }));

    Alert.alert('Success', 'Profile updated successfully!');
    setEditProfileModal(false);
  };

  // Navigation handlers
  const navigateToMyIssues = () => {
    // Navigate to Reports screen in Home stack
    (navigation as any).navigate('Home', { screen: 'Reports' });
  };

  const handleFavorites = () => {
    Alert.alert('Favorites', 'Your favorited issues will be displayed here');
  };

  // Help & Support handlers
  const handleHelp = () => {
    setHelpModal(true);
  };

  const callSupport = () => {
    Linking.openURL('tel:+911234567890');
  };

  const emailSupport = () => {
    Linking.openURL('mailto:support@jharkhand.gov.in');
  };

  // Privacy & Terms handlers
  const showPrivacyPolicy = () => {
    setPrivacyModal(true);
  };

  const showTerms = () => {
    setTermsModal(true);
  };

  const showAbout = () => {
    setAboutModal(true);
  };

  const getLanguageName = (code: string) => {
    switch (code) {
      case 'hi': return 'हिंदी';
      case 'bn': return 'বাংলা';
      default: return 'English';
    }
  };

  const menuItems = [
    {
      icon: 'person-outline',
      title: 'Edit Profile',
      subtitle: 'Update your personal information',
      onPress: handleEditProfile,
    },
    {
      icon: 'document-text-outline',
      title: 'My Issues',
      subtitle: 'View all your reported issues',
      onPress: navigateToMyIssues,
    },
    {
      icon: 'help-circle-outline',
      title: 'Help & Support',
      subtitle: 'Get help and contact support',
      onPress: handleHelp,
    },
    {
      icon: 'information-circle-outline',
      title: 'About',
      subtitle: 'App version and information',
      onPress: showAbout,
    },
    {
      icon: 'shield-checkmark-outline',
      title: 'Privacy Policy',
      subtitle: 'Read our privacy policy',
      onPress: showPrivacyPolicy,
    },
    {
      icon: 'document-outline',
      title: 'Terms of Service',
      subtitle: 'Read terms and conditions',
      onPress: showTerms,
    },
  ];

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Profile</Text>
        <Text style={styles.headerSubtitle}>Manage your account and preferences</Text>
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* User Info Card */}
        <View style={styles.userCard}>
          <View style={styles.avatar}>
            <Ionicons name="person" size={40} color="white" />
          </View>
          <View style={styles.userInfo}>
            <Text style={styles.userName}>{user?.name || 'User Name'}</Text>
            <Text style={styles.userEmail}>{user?.email || 'user@example.com'}</Text>
            <View style={styles.userRole}>
              <Ionicons name="shield-checkmark" size={14} color="#2E7D32" />
              <Text style={styles.roleText}>
                {user?.role === 'admin' ? 'Administrator' : 'Citizen'}
              </Text>
            </View>
          </View>
          <TouchableOpacity style={styles.editButton} onPress={handleEditProfile}>
            <Ionicons name="pencil" size={16} color="#2E7D32" />
          </TouchableOpacity>
        </View>


        {/* Settings Section */}
        <View style={styles.section}>
          <TouchableOpacity style={styles.settingItem} onPress={handleLanguageChange}>
            <View style={styles.settingLeft}>
              <Ionicons name="language-outline" size={20} color="#2E7D32" />
              <View style={styles.settingText}>
                <Text style={styles.settingTitle}>Language</Text>
                <Text style={styles.settingSubtitle}>
                  {getLanguageName(preferences.language)}
                </Text>
              </View>
            </View>
            <Ionicons name="chevron-forward" size={20} color="#666" />
          </TouchableOpacity>

          <View style={styles.settingItem}>
            <View style={styles.settingLeft}>
              <Ionicons name="notifications-outline" size={20} color="#2E7D32" />
              <View style={styles.settingText}>
                <Text style={styles.settingTitle}>Notifications</Text>
                <Text style={styles.settingSubtitle}>
                  Receive push notifications for updates and alerts
                </Text>
              </View>
            </View>
            <Switch
              value={notifications}
              onValueChange={setNotifications}
              trackColor={{ false: '#767577', true: '#A5D6A7' }}
              thumbColor={notifications ? '#2E7D32' : '#f4f3f4'}
            />
          </View>
        </View>

        {/* Menu Items */}
        <View style={styles.section}>

          {menuItems.map((item, index) => (
            <TouchableOpacity key={index} style={styles.menuItem} onPress={item.onPress}>
              <View style={styles.menuLeft}>
                <Ionicons name={item.icon as any} size={20} color="#2E7D32" />
                <View style={styles.menuText}>
                  <Text style={styles.menuTitle}>{item.title}</Text>
                  <Text style={styles.menuSubtitle}>{item.subtitle}</Text>
                </View>
              </View>
              <Ionicons name="chevron-forward" size={20} color="#666" />
            </TouchableOpacity>
          ))}
        </View>


        {/* Logout Button */}
        <TouchableOpacity style={styles.logoutButton} onPress={handleLogout}>
          <Ionicons name="log-out-outline" size={20} color="#f44336" />
          <Text style={styles.logoutText}>Logout</Text>
        </TouchableOpacity>

        <View style={styles.footer}>
          <Text style={styles.footerText}>
            © 2024 Government of Jharkhand
          </Text>
          <Text style={styles.footerSubtext}>
            Civic Issue Reporter - Making cities better together
          </Text>
        </View>
      </ScrollView>

      {/* Edit Profile Modal */}
      <Modal
        visible={editProfileModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setEditProfileModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>Edit Profile</Text>
            <TouchableOpacity onPress={saveProfile}>
              <Text style={styles.saveButton}>Save</Text>
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalContent}>
            <TouchableOpacity style={styles.imagePickerContainer} onPress={pickImage}>
              <View style={styles.imagePickerCircle}>
                {profileImage ? (
                  <Text style={styles.imagePickerText}>Image Selected</Text>
                ) : (
                  <>
                    <Ionicons name="camera" size={24} color="#2E7D32" />
                    <Text style={styles.imagePickerText}>Add Photo</Text>
                  </>
                )}
              </View>
            </TouchableOpacity>

            <View style={styles.formGroup}>
              <Text style={styles.formLabel}>Full Name</Text>
              <TextInput
                style={styles.formInput}
                value={editForm.name}
                onChangeText={(text) => setEditForm(prev => ({ ...prev, name: text }))}
                placeholder="Enter your full name"
              />
            </View>

            <View style={styles.formGroup}>
              <Text style={styles.formLabel}>Email</Text>
              <TextInput
                style={styles.formInput}
                value={editForm.email}
                onChangeText={(text) => setEditForm(prev => ({ ...prev, email: text }))}
                placeholder="Enter your email"
                keyboardType="email-address"
              />
            </View>

            <View style={styles.formGroup}>
              <Text style={styles.formLabel}>Phone Number</Text>
              <TextInput
                style={styles.formInput}
                value={editForm.phone}
                onChangeText={(text) => setEditForm(prev => ({ ...prev, phone: text }))}
                placeholder="Enter your phone number"
                keyboardType="phone-pad"
              />
            </View>

            <View style={styles.formGroup}>
              <Text style={styles.formLabel}>Address</Text>
              <TextInput
                style={[styles.formInput, styles.textArea]}
                value={editForm.address}
                onChangeText={(text) => setEditForm(prev => ({ ...prev, address: text }))}
                placeholder="Enter your address"
                multiline
                numberOfLines={3}
              />
            </View>
          </ScrollView>
        </SafeAreaView>
      </Modal>

      {/* Help & Support Modal */}
      <Modal
        visible={helpModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setHelpModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>Help & Support</Text>
            <View style={{ width: 24 }} />
          </View>

          <ScrollView style={styles.modalContent}>
            <View style={styles.helpSection}>
              <Text style={styles.helpTitle}>Contact Support</Text>
              <TouchableOpacity style={styles.helpOption} onPress={callSupport}>
                <Ionicons name="call" size={20} color="#2E7D32" />
                <Text style={styles.helpOptionText}>Call Support: +91 123 456 7890</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.helpOption} onPress={emailSupport}>
                <Ionicons name="mail" size={20} color="#2E7D32" />
                <Text style={styles.helpOptionText}>Email: support@jharkhand.gov.in</Text>
              </TouchableOpacity>
            </View>

            <View style={styles.helpSection}>
              <Text style={styles.helpTitle}>Frequently Asked Questions</Text>
              <Text style={styles.faqQuestion}>How do I report an issue?</Text>
              <Text style={styles.faqAnswer}>Tap the "Report Issue" button on the home screen and fill out the form with details about the problem.</Text>

              <Text style={styles.faqQuestion}>How can I track my reported issues?</Text>
              <Text style={styles.faqAnswer}>Go to "My Issues" in your profile to see all your reported issues and their current status.</Text>

              <Text style={styles.faqQuestion}>What if my issue is not resolved?</Text>
              <Text style={styles.faqAnswer}>You can follow up on your issue or contact support directly using the contact options above.</Text>
            </View>
          </ScrollView>
        </SafeAreaView>
      </Modal>

      {/* Privacy Policy Modal */}
      <Modal
        visible={privacyModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setPrivacyModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>Privacy Policy</Text>
            <View style={{ width: 24 }} />
          </View>

          <ScrollView style={styles.modalContent}>
            <Text style={styles.policyText}>
              <Text style={styles.policyTitle}>Government of Jharkhand - Civic Issue Reporter{'\n\n'}</Text>

              <Text style={styles.policySubtitle}>Data Collection{'\n'}</Text>
              We collect information you provide when reporting civic issues, including location data, photos, and contact information. This data is used solely for addressing civic concerns and improving public services.{'\n\n'}

              <Text style={styles.policySubtitle}>Data Usage{'\n'}</Text>
              Your data is used to process and resolve reported issues, communicate updates, and improve our services. We do not sell or share your personal information with third parties.{'\n\n'}

              <Text style={styles.policySubtitle}>Data Security{'\n'}</Text>
              We implement appropriate security measures to protect your information against unauthorized access, alteration, disclosure, or destruction.{'\n\n'}

              <Text style={styles.policySubtitle}>Your Rights{'\n'}</Text>
              You have the right to access, update, or delete your personal information. Contact us through the support channels for any privacy-related concerns.
            </Text>
          </ScrollView>
        </SafeAreaView>
      </Modal>

      {/* Terms of Service Modal */}
      <Modal
        visible={termsModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setTermsModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>Terms of Service</Text>
            <View style={{ width: 24 }} />
          </View>

          <ScrollView style={styles.modalContent}>
            <Text style={styles.policyText}>
              <Text style={styles.policyTitle}>Terms of Service{'\n\n'}</Text>

              <Text style={styles.policySubtitle}>Acceptance of Terms{'\n'}</Text>
              By using this application, you agree to comply with these terms of service and all applicable laws and regulations.{'\n\n'}

              <Text style={styles.policySubtitle}>Proper Use{'\n'}</Text>
              This application is intended for reporting legitimate civic issues. Misuse, including false reports or spam, may result in account suspension.{'\n\n'}

              <Text style={styles.policySubtitle}>Content Guidelines{'\n'}</Text>
              All submitted content must be respectful, accurate, and relevant to civic issues. Inappropriate content will be removed.{'\n\n'}

              <Text style={styles.policySubtitle}>Government Services{'\n'}</Text>
              This app facilitates communication with government services but does not guarantee specific response times or outcomes.{'\n\n'}

              <Text style={styles.policySubtitle}>Modifications{'\n'}</Text>
              We reserve the right to modify these terms at any time. Continued use constitutes acceptance of updated terms.
            </Text>
          </ScrollView>
        </SafeAreaView>
      </Modal>

      {/* About Modal */}
      <Modal
        visible={aboutModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setAboutModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>About</Text>
            <View style={{ width: 24 }} />
          </View>

          <ScrollView style={styles.modalContent}>
            <View style={styles.aboutSection}>
              <View style={styles.appIconContainer}>
                <View style={styles.appIcon}>
