import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
  Switch,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector, useDispatch } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation } from '@react-navigation/native';
import { RootState } from '../../store/store';
import { logout } from '../../store/slices/authSlice';
import WorkerHeader from '../../components/WorkerHeader';
import notificationService, { NotificationSettings } from '../../services/notificationService';

interface WorkerProfile {
  id: string;
  workerId: string;
  name: string;
  email: string;
  phone: string;
  department: string;
  skills: string[];
  assignedArea: string;
  joiningDate: string;
  status: string;
  profilePhoto?: string;
}

interface Performance {
  totalAssignments: number;
  completedAssignments: number;
  completionRate: number;
  avgResponseTime: number;
  rating: number;
}

const WorkerProfileScreen = () => {
  const { user, token } = useSelector((state: RootState) => state.auth);
  const dispatch = useDispatch();
  const navigation = useNavigation();

  const [profile, setProfile] = useState<WorkerProfile | null>(null);
  const [notificationSettings, setNotificationSettings] = useState<NotificationSettings | null>(null);
  const [notificationStatus, setNotificationStatus] = useState<{
    registered: boolean;
    pushToken: string | null;
  }>({ registered: false, pushToken: null });
  const [performance, setPerformance] = useState<Performance | null>(null);
  const [loading, setLoading] = useState(false);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);
  const [locationEnabled, setLocationEnabled] = useState(true);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    if (!user?.id) return;

    setLoading(true);
    try {
      console.log('Fetching profile for user:', user.id);
      const response = await fetch(`http://192.168.29.36:3003/api/v1/workers/profile/${user.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();
      console.log('Profile API response:', data);

      if (data.success) {
        setProfile(data.data.worker);
        setPerformance(data.data.performance);
      } else {
        console.error('Profile API error:', data.error);
        Alert.alert('Error', data.error?.message || 'Failed to fetch profile');
      }
    } catch (error) {
      console.error('Profile fetch error:', error);
      Alert.alert('Error', 'Network error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    Alert.alert(
      'Logout',
      'Are you sure you want to logout?',
      [
        { text: 'Cancel', style: 'cancel' },
        { 
          text: 'Logout', 
          style: 'destructive',
          onPress: () => dispatch(logout())
        }
      ]
    );
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const renderProfileHeader = () => (
    <View style={styles.profileHeader}>
      <View style={styles.avatarContainer}>
        <View style={styles.avatar}>
          <Ionicons name="person" size={40} color="#FF6B35" />
        </View>
        <TouchableOpacity style={styles.editAvatarButton}>
          <Ionicons name="camera" size={16} color="white" />
        </TouchableOpacity>
      </View>

      <View style={styles.profileInfo}>
        <Text style={styles.profileName}>{profile?.name || user?.name}</Text>
        <Text style={styles.profileId}>ID: {profile?.workerId || user?.workerId}</Text>
        <Text style={styles.profileDepartment}>{profile?.department || user?.department}</Text>

        <View style={styles.ratingContainer}>
          <Ionicons name="star" size={16} color="#FFD700" />
          <Text style={styles.rating}>{performance?.rating?.toFixed(1) || '0.0'}</Text>
          <Text style={styles.ratingText}>Rating</Text>
        </View>
      </View>
    </View>
  );

  const renderPerformanceStats = () => (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Performance Overview</Text>
      <View style={styles.statsGrid}>
        <View style={styles.statCard}>
          <Text style={styles.statNumber}>{performance?.totalAssignments || 0}</Text>
          <Text style={styles.statLabel}>Total Assignments</Text>
        </View>
        <View style={styles.statCard}>
          <Text style={styles.statNumber}>{performance?.completedAssignments || 0}</Text>
          <Text style={styles.statLabel}>Completed</Text>
        </View>
        <View style={styles.statCard}>
          <Text style={styles.statNumber}>{performance?.completionRate?.toFixed(0) || 0}%</Text>
          <Text style={styles.statLabel}>Success Rate</Text>
        </View>
        <View style={styles.statCard}>
          <Text style={styles.statNumber}>{performance?.avgResponseTime?.toFixed(1) || 0}h</Text>
          <Text style={styles.statLabel}>Avg Response</Text>
        </View>
      </View>
    </View>
  );

  const renderPersonalInfo = () => (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Personal Information</Text>

      <View style={styles.infoCard}>
        <View style={styles.infoRow}>
          <Ionicons name="mail" size={20} color="#666" />
          <View style={styles.infoContent}>
            <Text style={styles.infoLabel}>Email</Text>
            <Text style={styles.infoValue}>{profile?.email || user?.email}</Text>
          </View>
        </View>

        <View style={styles.infoRow}>
          <Ionicons name="call" size={20} color="#666" />
          <View style={styles.infoContent}>
            <Text style={styles.infoLabel}>Phone</Text>
            <Text style={styles.infoValue}>{profile?.phone || user?.phone}</Text>
          </View>
        </View>

        <View style={styles.infoRow}>
          <Ionicons name="business" size={20} color="#666" />
          <View style={styles.infoContent}>
            <Text style={styles.infoLabel}>Department</Text>
            <Text style={styles.infoValue}>{profile?.department || user?.department}</Text>
          </View>
        </View>

        <View style={styles.infoRow}>
          <Ionicons name="location" size={20} color="#666" />
          <View style={styles.infoContent}>
            <Text style={styles.infoLabel}>Assigned Area</Text>
            <Text style={styles.infoValue}>{profile?.assignedArea || user?.assignedArea}</Text>
          </View>
        </View>

        <View style={styles.infoRow}>
          <Ionicons name="calendar" size={20} color="#666" />
          <View style={styles.infoContent}>
            <Text style={styles.infoLabel}>Joining Date</Text>
            <Text style={styles.infoValue}>
              {profile?.joiningDate ? formatDate(profile.joiningDate) : 'N/A'}
            </Text>
          </View>
        </View>
      </View>
    </View>
  );

  const renderSkills = () => (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Skills & Expertise</Text>
      <View style={styles.skillsContainer}>
        {(profile?.skills || user?.skills || []).map((skill, index) => (
          <View key={index} style={styles.skillChip}>
            <Text style={styles.skillText}>{skill}</Text>
          </View>
        ))}
        {(!profile?.skills && !user?.skills) && (
          <Text style={styles.noSkillsText}>No skills added yet</Text>
        )}
      </View>
    </View>
  );

  const renderSettings = () => (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Settings</Text>

      <View style={styles.settingsCard}>
        <View style={styles.settingRow}>
          <View style={styles.settingInfo}>
            <Ionicons name="notifications" size={20} color="#666" />
            <View style={styles.settingContent}>
              <Text style={styles.settingLabel}>Push Notifications</Text>
              <Text style={styles.settingDescription}>Receive alerts for new assignments</Text>
            </View>
          </View>
          <Switch
            value={notificationsEnabled}
            onValueChange={setNotificationsEnabled}
            trackColor={{ false: '#ccc', true: '#FF6B35' }}
            thumbColor={notificationsEnabled ? '#fff' : '#f4f3f4'}
          />
        </View>

        <View style={styles.settingRow}>
          <View style={styles.settingInfo}>
            <Ionicons name="location" size={20} color="#666" />
            <View style={styles.settingContent}>
              <Text style={styles.settingLabel}>Location Services</Text>
              <Text style={styles.settingDescription}>Allow location tracking for work</Text>
            </View>
          </View>
          <Switch
            value={locationEnabled}
            onValueChange={setLocationEnabled}
            trackColor={{ false: '#ccc', true: '#FF6B35' }}
            thumbColor={locationEnabled ? '#fff' : '#f4f3f4'}
          />
        </View>
      </View>
    </View>
  );

  const renderActions = () => (
    <View style={styles.section}>
      <TouchableOpacity 
        style={styles.actionButton}
        onPress={() => (navigation as any).navigate('EditProfile')}
      >
        <Ionicons name="create" size={20} color="#FF6B35" />
        <Text style={styles.actionButtonText}>Edit Profile</Text>
        <Ionicons name="chevron-forward" size={16} color="#ccc" />
      </TouchableOpacity>

      <TouchableOpacity 
        style={styles.actionButton}
        onPress={() => (navigation as any).navigate('WorkHistory')}
      >
        <Ionicons name="document-text" size={20} color="#FF6B35" />
        <Text style={styles.actionButtonText}>Work History</Text>
        <Ionicons name="chevron-forward" size={16} color="#ccc" />
      </TouchableOpacity>

      <TouchableOpacity 
        style={styles.actionButton}
        onPress={() => (navigation as any).navigate('HelpSupport')}
      >
        <Ionicons name="help-circle" size={20} color="#FF6B35" />
        <Text style={styles.actionButtonText}>Help & Support</Text>
        <Ionicons name="chevron-forward" size={16} color="#ccc" />
      </TouchableOpacity>

      <TouchableOpacity 
        style={styles.actionButton}
        onPress={() => (navigation as any).navigate('AboutApp')}
      >
        <Ionicons name="information-circle" size={20} color="#FF6B35" />
        <Text style={styles.actionButtonText}>About App</Text>
        <Ionicons name="chevron-forward" size={16} color="#ccc" />
      </TouchableOpacity>

      <TouchableOpacity style={[styles.actionButton, styles.logoutButton]} onPress={handleLogout}>
        <Ionicons name="log-out" size={20} color="#f44336" />
        <Text style={[styles.actionButtonText, styles.logoutText]}>Logout</Text>
        <Ionicons name="chevron-forward" size={16} color="#ccc" />
      </TouchableOpacity>
    </View>
  );

  const insets = useSafeAreaInsets();

  const headerRightComponent = (
    <TouchableOpacity>
      <Ionicons name="settings" size={24} color="white" />
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <WorkerHeader
        title="Profile"
        rightComponent={headerRightComponent}
      />

      <ScrollView 
        style={styles.scrollView} 
        contentContainerStyle={[styles.scrollContent, { paddingBottom: insets.bottom + 80 }]}
        showsVerticalScrollIndicator={false}
      >
        {renderProfileHeader()}
        {renderPerformanceStats()}
        {renderPersonalInfo()}
        {renderSkills()}
        {renderSettings()}
        {renderActions()}

        {/* App Version */}
        <View style={styles.versionContainer}>
          <Text style={styles.versionText}>Civic Reporter Worker v1.0.0</Text>
        </View>
      </ScrollView>
    </View>
  );
};

