import React, { useState, useEffect, useCallback, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  RefreshControl,
  Alert,
  ActivityIndicator,
  TextInput,
  Dimensions,
  Image,
  Platform,
  Animated,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { apiService } from '../../services/apiService';
import { getCurrentConfig } from '../../config/environment';

const { width, height } = Dimensions.get('window');

interface AlertItem {
  id: string;
  title: string;
  description: string;
  priority: 'high' | 'medium' | 'low';
  type: 'emergency' | 'maintenance' | 'announcement' | 'warning';
  targetAudience: 'all' | 'citizens' | 'department' | 'admins';
  department?: string;
  createdAt: string;
  expiresAt?: string;
  status: 'active' | 'expired';
  readBy: string[];
  createdBy: string;
}

const AlertsScreen = () => {
  const [alerts, setAlerts] = useState<AlertItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [currentUserId, setCurrentUserId] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [activeTab, setActiveTab] = useState<'all' | 'important'>('all');
  const [newAlertIds, setNewAlertIds] = useState<Set<string>>(new Set());
  const [toastVisible, setToastVisible] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [userToken, setUserToken] = useState<string | null>(null);
  const [expandedAlerts, setExpandedAlerts] = useState<Set<string>>(new Set());

  const wsRef = useRef<WebSocket | null>(null);
  const toastOpacity = useRef(new Animated.Value(0)).current;
  const toastTranslateY = useRef(new Animated.Value(50)).current;
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // All API calls now go through apiService

  // Get current user ID and token
  useEffect(() => {
    const getCurrentUserData = async () => {
      try {
        const userDataString = await AsyncStorage.getItem('userData');
        const userData = userDataString ? JSON.parse(userDataString) : {};
        setCurrentUserId(userData.id || null);
        
        // Get JWT token for WebSocket authentication
        const token = await AsyncStorage.getItem('token');
        setUserToken(token);
      } catch (error) {
        console.error('Error getting user data:', error);
      }
    };
    getCurrentUserData();
  }, []);

  const fetchAlerts = useCallback(async () => {
    try {
      const res = await apiService.getPublicAlerts();
      if (res.success) {
        // Sort alerts by creation date - newest first
        const sortedAlerts = (res.data || []).sort((a, b) => 
          new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );
        setAlerts(sortedAlerts);
      } else {
        console.error('Failed to fetch alerts:', res.error);
        setAlerts([]);
      }
    } catch (error) {
      console.error('Error fetching alerts:', error);
      setAlerts([]);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, []);

  useEffect(() => {
    fetchAlerts();
    
    // Connect to WebSocket when user token is available
    if (userToken) {
      connectWebSocket();
    }
    
    return () => {
      // Cleanup WebSocket connection
      if (wsRef.current) {
        wsRef.current.close(1000); // Normal closure
      }
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
    };
  }, [fetchAlerts, userToken]);

  const connectWebSocket = useCallback(() => {
    // Don't connect if no token available
    if (!userToken) {
      console.log('WebSocket connection skipped: No authentication token');
      return;
    }

    // Don't reconnect if max attempts reached
    if (reconnectAttempts.current >= maxReconnectAttempts) {
      console.log('WebSocket max reconnection attempts reached');
      return;
    }

    try {
      const config = getCurrentConfig();
      const wsUrl = `${config.WS_URL}?token=${encodeURIComponent(userToken)}`;
      
      console.log('Attempting WebSocket connection...');
      wsRef.current = new WebSocket(wsUrl);
      
      wsRef.current.onopen = () => {
        console.log('âœ… WebSocket connected successfully');
        reconnectAttempts.current = 0; // Reset attempts on successful connection
        
        // Clear any pending reconnection timeout
        if (reconnectTimeoutRef.current) {
          clearTimeout(reconnectTimeoutRef.current);
          reconnectTimeoutRef.current = null;
        }
      };
      
      wsRef.current.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          console.log('ðŸ“¨ WebSocket message received:', data.type);
          
          if (data.type === 'new_alert' && data.alert) {
            handleNewAlert(data.alert);
          } else if (data.type === 'connection') {
            console.log('ðŸ”Œ WebSocket connection confirmed:', data.message);
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };
      
      wsRef.current.onerror = (error) => {
        console.error('âŒ WebSocket error:', error);
      };
      
      wsRef.current.onclose = (event) => {
        console.log(`ðŸ”Œ WebSocket disconnected (code: ${event.code})`);
        
        // Only attempt reconnection if not manually closed and within attempt limits
        if (event.code !== 1000 && reconnectAttempts.current < maxReconnectAttempts) {
          reconnectAttempts.current++;
          
          // Exponential backoff: 2^attempts * 1000ms (1s, 2s, 4s, 8s, 16s)
          const delay = Math.pow(2, reconnectAttempts.current - 1) * 1000;
          
          console.log(`ðŸ”„ Reconnecting in ${delay}ms (attempt ${reconnectAttempts.current}/${maxReconnectAttempts})`);
          
          reconnectTimeoutRef.current = setTimeout(() => {
            if (wsRef.current?.readyState === WebSocket.CLOSED) {
              connectWebSocket();
            }
          }, delay);
        } else if (reconnectAttempts.current >= maxReconnectAttempts) {
          console.log('ðŸš« Max WebSocket reconnection attempts reached');
        }
      };
    } catch (error) {
      console.error('Error connecting to WebSocket:', error);
      reconnectAttempts.current++;
    }
  }, [userToken]);

  const handleNewAlert = useCallback((newAlert: AlertItem) => {
    setAlerts(prevAlerts => {
      // Check if alert already exists
      const exists = prevAlerts.some(alert => alert.id === newAlert.id);
      if (exists) return prevAlerts;
      
      // Add to new alert IDs for animation
      setNewAlertIds(prev => new Set([...prev, newAlert.id]));
      
      // Remove from new alert IDs after animation
      setTimeout(() => {
        setNewAlertIds(prev => {
          const newSet = new Set(prev);
          newSet.delete(newAlert.id);
          return newSet;
        });
      }, 2000);
      
      // Show toast notification
      showToast('New alert received!');
      
      // Add new alert to the top of the list
      return [newAlert, ...prevAlerts];
    });
  }, []);

  const showToast = useCallback((message: string) => {
    setToastMessage(message);
    setToastVisible(true);
    
    // Animate toast in
    Animated.parallel([
      Animated.timing(toastOpacity, {
        toValue: 1,
        duration: 300,
        useNativeDriver: true,
      }),
      Animated.timing(toastTranslateY, {
        toValue: 0,
        duration: 300,
        useNativeDriver: true,
      }),
    ]).start();
    
    // Hide toast after 3 seconds
    setTimeout(() => {
      Animated.parallel([
        Animated.timing(toastOpacity, {
          toValue: 0,
          duration: 300,
          useNativeDriver: true,
        }),
        Animated.timing(toastTranslateY, {
          toValue: 50,
          duration: 300,
          useNativeDriver: true,
        }),
      ]).start(() => {
        setToastVisible(false);
      });
    }, 3000);
  }, [toastOpacity, toastTranslateY]);

  const markAsRead = async (alertId: string) => {
    try {
      const res = await apiService.markAlertAsRead(alertId);
      if (res.success) {
        setAlerts(prev => prev.map(alert => {
          if (alert.id === alertId && currentUserId) {
            return { ...alert, readBy: [...alert.readBy, currentUserId] };
          }
          return alert;
        }));
      }
    } catch (error) {
      console.error('Error marking alert as read:', error);
    }
  };

  const isAlertRead = (alert: AlertItem): boolean => {
    if (!currentUserId) return false;
    return alert.readBy.includes(currentUserId);
  };

  const getAlertIcon = (type: string) => {
    switch (type) {
      case 'emergency': return 'warning';
      case 'maintenance': return 'construct';
      case 'announcement': return 'megaphone';
      case 'warning': return 'alert-circle';
      default: return 'information-circle';
    }
  };

  const getAlertIconColor = (type: string) => {
    switch (type) {
      case 'emergency': return '#FF4444';
      case 'maintenance': return '#FF8800';
      case 'announcement': return '#2E7D32';
      case 'warning': return '#FF6B00';
      default: return '#2196F3';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#FF4444';
      case 'medium': return '#FF8800';
      case 'low': return '#00AA00';
      default: return '#666666';
    }
  };

  const getAlertTypeColor = (type: string) => {
    switch (type) {
      case 'emergency': return '#FF4444';
      case 'maintenance': return '#FF8800';
      case 'announcement': return '#2E7D32';
      case 'warning': return '#FF6B00';
      default: return '#666666';
    }
  };

  const getAlertTypeBadgeText = (type: string) => {
    switch (type) {
      case 'emergency': return 'EMERGENCY';
      case 'maintenance': return 'MAINTENANCE';
      case 'announcement': return 'ANNOUNCEMENT';
      case 'warning': return 'WARNING';
      default: return type.toUpperCase();
    }
  };

  const onRefresh = useCallback(async () => {
    setRefreshing(true);
    await fetchAlerts();
  }, [fetchAlerts]);

  const filteredAlerts = alerts.filter(alert => {
    // Search filter
    if (searchQuery) {
      const matchesSearch = alert.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                           alert.description.toLowerCase().includes(searchQuery.toLowerCase());
      if (!matchesSearch) return false;
    }

    // Tab filter - only important tab now
    if (activeTab === 'important' && alert.priority !== 'high') {
      return false;
    }

    return true;
  });

  const handleAlertPress = (alert: AlertItem) => {
    // Mark as read if not already read
    if (!isAlertRead(alert)) {
      markAsRead(alert.id);
    }
    
    // Show alert details
    Alert.alert(
      alert.title,
      alert.description,
      [
        { text: 'OK', style: 'default' }
      ]
    );
  };

  const toggleAlertExpansion = (alertId: string) => {
    setExpandedAlerts(prev => {
      const newSet = new Set(prev);
      if (newSet.has(alertId)) {
        newSet.delete(alertId);
      } else {
        newSet.add(alertId);
      }
      return newSet;
    });
  };

  const isAlertExpanded = (alertId: string) => {
    return expandedAlerts.has(alertId);
  };

  const renderAlert = (alert: AlertItem) => {
    const isRead = isAlertRead(alert);
    const isHighPriority = alert.priority === 'high';
    const typeColor = getAlertTypeColor(alert.type);
    const isExpanded = isAlertExpanded(alert.id);
    const shouldShowReadMore = alert.description.length > 150;
    const isNewAlert = newAlertIds.has(alert.id);
    
    return (
      <View
        key={alert.id}
        style={[
          styles.alertCard,
          !isRead && styles.unreadAlert,
          isNewAlert && styles.newAlertCard,
          { borderLeftColor: typeColor, borderLeftWidth: 4 }
        ]}
      >
        {/* Severity Indicator - Colored Side Border is handled in style above */}
        
        {/* Priority Badge */}
        <View style={[
          styles.priorityBadge, 
          { backgroundColor: getPriorityColor(alert.priority) }
        ]}>
          <Text style={styles.priorityBadgeText}>
            {alert.priority.toUpperCase()}
          </Text>
        </View>
        
        {/* Type Badge */}
        <View style={[styles.typeBadge, { backgroundColor: typeColor }]}>
          <Text style={styles.typeBadgeText}>{getAlertTypeBadgeText(alert.type)}</Text>
        </View>
        
        <View style={styles.alertHeader}>
          <View style={[
            styles.alertIconContainer,
            { backgroundColor: typeColor }
          ]}>
            <Ionicons
              name={getAlertIcon(alert.type) as any}
              size={24}
              color="#fff"
            />
          </View>
          <View style={styles.alertInfo}>
            <Text style={[styles.alertTitle, !isRead && styles.unreadText]}>
              {alert.title}
            </Text>
            <Text style={styles.alertMetaText}>
              {alert.department || 'General'} â€¢ {formatTimeAgo(alert.createdAt)}
            </Text>
          </View>
          
          {/* Read Status Indicator */}
          <View style={styles.alertStatus}>
            <View style={[
              styles.readStatusDot,
              isRead ? styles.readDot : { backgroundColor: typeColor }
            ]} />
          </View>
        </View>
        
        <View style={styles.alertContent}>
          <Text 
            style={[
              styles.alertDescription,
              !isRead && styles.unreadDescription
            ]} 
            numberOfLines={isExpanded ? undefined : 3}
          >
            {alert.description}
          </Text>
          
          {shouldShowReadMore && (
            <TouchableOpacity 
              style={styles.readMoreButton}
              onPress={() => toggleAlertExpansion(alert.id)}
            >
              <Text style={[styles.readMoreText, { color: typeColor }]}>
                {isExpanded ? 'Read Less' : 'Read More'}
              </Text>
              <Ionicons 
                name={isExpanded ? 'chevron-up' : 'chevron-down'} 
                size={14} 
                color={typeColor}
                style={styles.readMoreIcon}
              />
            </TouchableOpacity>
          )}
        </View>
        
        <View style={styles.alertFooter}>
          <View style={[styles.timeInfoBar, { backgroundColor: `${typeColor}10` }]}>
            <View style={styles.timeItem}>
              <Ionicons name="time" size={14} color="#666" />
              <Text style={styles.timeText}>{formatTimeAgo(alert.createdAt)}</Text>
            </View>
            {alert.expiresAt && (
              <>
                <Text style={styles.timeSeparator}>â€¢</Text>
                <View style={styles.timeItem}>
                  <Ionicons name="calendar" size={14} color="#FF8800" />
                  <Text style={styles.expiryText}>
                    Expires {new Date(alert.expiresAt).toLocaleDateString('en-IN', {
                      day: 'numeric',
                      month: 'short'
                    })}
                  </Text>
                </View>
              </>
            )}
          </View>
          
          {/* Quick Action Button */}
          {!isRead && (
            <TouchableOpacity 
              style={[styles.markReadButton, { backgroundColor: typeColor }]}
              onPress={() => markAsRead(alert.id)}
            >
              <Text style={styles.markReadText}>Mark Read</Text>
            </TouchableOpacity>
          )}
        </View>
      </View>
    );
  };

  const formatTimeAgo = (dateString: string) => {
    const now = new Date();
    const alertDate = new Date(dateString);
    const diffInMinutes = Math.floor((now.getTime() - alertDate.getTime()) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return `${Math.floor(diffInMinutes / 1440)}d ago`;
  };

  if (loading) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#007AFF" />
          <Text style={styles.loadingText}>Loading alerts...</Text>
        </View>
      </SafeAreaView>
    );
  }
  return (
    <View style={styles.container}>
      {/* Clean Green Header matching Issues page */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Alerts & Notifications</Text>
        <Text style={styles.headerSubtitle}>Stay updated with important alerts</Text>
      </View>

      {/* Tab Navigation */}
      <View style={styles.tabContainer}>
        <TouchableOpacity 
          style={[styles.tab, activeTab === 'all' && styles.activeTab]}
          onPress={() => setActiveTab('all')}
        >
          <View style={{ flexDirection: 'row', alignItems: 'center' }}>
            <Ionicons name={activeTab === 'all' ? 'notifications' : 'notifications-outline'} size={18} color={activeTab === 'all' ? '#2E7D32' : '#666'} style={styles.tabIcon} />
            <Text style={[styles.tabText, activeTab === 'all' && styles.activeTabText]}>All Alerts</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity 
          style={[styles.tab, activeTab === 'important' && styles.activeTab]}
          onPress={() => setActiveTab('important')}
        >
          <View style={{ flexDirection: 'row', alignItems: 'center' }}>
            <Ionicons name={activeTab === 'important' ? 'warning' : 'warning-outline'} size={18} color={activeTab === 'important' ? '#FF4444' : '#666'} style={styles.tabIcon} />
            <Text style={[styles.tabText, activeTab === 'important' && styles.activeTabText]}>Important</Text>
            {alerts.filter(a => a.priority === 'high').length > 0 && (
              <View style={styles.importantBadge}>
                <Text style={styles.importantBadgeText}>{alerts.filter(a => a.priority === 'high').length}</Text>
              </View>
            )}
          </View>
        </TouchableOpacity>
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <Ionicons name="search" size={20} color="#999" style={styles.searchIcon} />
        <TextInput
          style={styles.searchInput}
          placeholder="Search alerts..."
          placeholderTextColor="#999"
          value={searchQuery}
          onChangeText={setSearchQuery}
        />
      </View>

      {/* Alerts List */}
      <ScrollView
        style={styles.alertsList}
        refreshControl={
          <RefreshControl 
            refreshing={refreshing} 
            onRefresh={onRefresh}
            colors={['#2E7D32']}
            tintColor="#2E7D32"
          />
        }
        showsVerticalScrollIndicator={false}
      >
        {/* Results Count */}
        <Text style={styles.resultsCount}>{filteredAlerts.length} alerts found</Text>
        
        {filteredAlerts.length === 0 ? (
          <View style={styles.emptyContainer}>
            <View style={styles.emptyIconContainer}>
              <Ionicons 
                name={activeTab === 'important' ? "warning-outline" : "notifications-off"} 
                size={80} 
                color="#ddd" 
              />
            </View>
            <Text style={styles.emptyText}>
              {activeTab === 'important' ? 'No important alerts' : 'No alerts found'}
            </Text>
            <Text style={styles.emptySubtext}>
              {searchQuery
                ? 'Try adjusting your search terms'
                : activeTab === 'important'
                ? 'High priority alerts will appear here'
                : 'New alerts will appear here when available'}
            </Text>
          </View>
        ) : (
          <>
            {activeTab === 'important' && filteredAlerts.length > 0 && (
              <View style={styles.sectionHeader}>
                <Ionicons name="warning" size={20} color="#FF4444" />
                <Text style={styles.sectionTitle}>High Priority Alerts</Text>
              </View>
            )}
            {filteredAlerts.map(renderAlert)}
          </>
        )}
      </ScrollView>

      {/* Toast Notification */}
      {toastVisible && (
        <Animated.View
          style={[
            styles.toast,
            {
              opacity: toastOpacity,
              transform: [{ translateY: toastTranslateY }],
            },
          ]}
        >
          <Ionicons name="checkmark-circle" size={20} color="#fff" style={styles.toastIcon} />
          <Text style={styles.toastText}>{toastMessage}</Text>
        </Animated.View>
      )}
    </View>
  );
};

