import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Alert,
  KeyboardAvoidingView,
  Platform,
  Image,
  ScrollView,
  Dimensions,
  Modal,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { useDispatch } from 'react-redux';
import { useNavigation } from '@react-navigation/native';
import { useTranslation } from 'react-i18next';
import { loginSuccess } from '../../store/slices/authSlice';
import { tokenStorage } from '../../utils/tokenStorage';
import { apiService } from '../../services/apiService';
import { signInWithGoogle } from '../../services/firebaseAuth';

const RegisterScreen = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [termsModal, setTermsModal] = useState(false);
  const [registrationType, setRegistrationType] = useState<'email' | 'phone'>('email');
  
  const dispatch = useDispatch();
  const navigation = useNavigation();
  const { t } = useTranslation();
  
  // Initialize Google Auth hook
  // No hooks needed for pure Firebase Google auth

  // Get screen dimensions for responsive design
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');
  const isTablet = screenWidth >= 768;
  const isSmallPhone = screenWidth < 350;
  const isLandscape = screenWidth > screenHeight;

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  // Tab selection handler
  const handleTabSelect = (type: 'email' | 'phone') => {
    setRegistrationType(type);
    // Don't clear fields to prevent UI movement
  };

  const handleEmailRegister = async () => {
    const { name, email, password, confirmPassword } = formData;
    
    if (!name || !email || !password || !confirmPassword) {
      Alert.alert(t('common.error'), t('auth.fillAllFields'));
      return;
    }

    if (password !== confirmPassword) {
      Alert.alert(t('common.error'), t('auth.passwordMismatch'));
      return;
    }

    if (password.length < 6) {
      Alert.alert(t('common.error'), t('auth.passwordTooShort'));
      return;
    }

    setLoading(true);
    
    try {
      // Register with backend API
      const response = await apiService.register({
        name: name,
        email: email,
        phone: formData.phone || '',
        password: password,
        confirmPassword: confirmPassword,
        district: '', // Will be updated when location features are added
        block: '', // Will be updated when location features are added
      });
      
      if (response.success && response.data) {
        Alert.alert(
          'Registration Successful!', 
          'A verification email has been sent to your email address. Please check your inbox and click the verification link to activate your account.',
          [
            {
              text: 'OK',
              onPress: () => (navigation as any).navigate('EmailVerification', { email: email }),
            },
          ]
        );
      } else {
        Alert.alert(t('common.error'), response.error?.message || 'Registration failed. Please try again.');
      }
    } catch (error: any) {
      console.error('Email registration error:', error);
      Alert.alert(t('common.error'), error.message || t('auth.registrationError'));
    } finally {
      setLoading(false);
    }
  };

  const handlePhoneRegister = async () => {
    const { name, phone, password, confirmPassword } = formData;
    
    if (!name || !phone || !password || !confirmPassword) {
      Alert.alert(t('common.error'), t('auth.fillAllFields'));
      return;
    }

    if (password !== confirmPassword) {
      Alert.alert(t('common.error'), t('auth.passwordMismatch'));
      return;
    }

    if (password.length < 6) {
      Alert.alert(t('common.error'), t('auth.passwordTooShort'));
      return;
    }

    setLoading(true);
    
    try {
      // Register with backend API (phone-based registration)
      const response = await apiService.register({
        name: name,
        email: formData.email || '',
        phone: phone,
        password: password,
        confirmPassword: confirmPassword,
        district: '', // Will be updated when location features are added
        block: '', // Will be updated when location features are added
      });
      
      if (response.success && response.data) {
        // Send OTP to phone number
        const otpResponse = await apiService.sendPhoneOTP(phone);
        
        if (otpResponse.success) {
          Alert.alert(
            'Registration Successful!', 
            'An OTP has been sent to your phone number. Please verify to complete registration.',
            [
              {
                text: 'OK',
                onPress: () => (navigation as any).navigate('PhoneVerification', { phoneNumber: phone }),
              },
            ]
          );
        } else {
          Alert.alert(t('common.error'), 'Failed to send OTP. Please try again.');
        }
      } else {
        Alert.alert(t('common.error'), response.error?.message || 'Registration failed. Please try again.');
      }
    } catch (error: any) {
      console.error('Phone registration error:', error);
      Alert.alert(t('common.error'), error.message || t('auth.registrationError'));
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleRegister = async () => {
    try {
      setLoading(true);
      console.log('[RegisterScreen] Starting Firebase Google Sign-In...');
      
      // Use pure Firebase Google Sign-In
      const user = await signInWithGoogle();
      if (!user) return;

      const idToken = await user.getIdToken();
      const response = await apiService.loginWithGoogle(idToken);

      if (response.success && response.data) {
        const userData = {
          id: response.data.user.id,
          name: response.data.user.fullName || response.data.user.name || '',
          email: response.data.user.email,
          phone: response.data.user.phoneNumber || response.data.user.phone || '',
          role: (response.data.user.role === 'admin' || response.data.user.role === 'ADMIN') ? 'admin' as const : 'citizen' as const,
        };
        
        await tokenStorage.storeAuthData(response.data.token, userData);
        
        dispatch(loginSuccess({
          user: userData,
          token: response.data.token,
        }));
        
        Alert.alert('Success', 'Successfully registered/logged in with Google!');
      } else {
        Alert.alert('Error', 'Google registration failed. Please try again.');
      }
    } catch (error: any) {
      console.error('Google registration error:', error);
      Alert.alert('Error', 'Google registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleTermsPress = () => {
    setTermsModal(true);
  };

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView 
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.keyboardView}
        keyboardVerticalOffset={Platform.OS === 'ios' ? 0 : 0}
      >
        <ScrollView 
          contentContainerStyle={[
            styles.scrollContent,
            {
              paddingHorizontal: isTablet ? screenWidth * 0.15 : isSmallPhone ? 15 : 20,
              paddingTop: isLandscape ? 10 : 20,
              paddingBottom: 20,
            }
          ]}
          keyboardShouldPersistTaps="handled"
          showsVerticalScrollIndicator={true}
          showsHorizontalScrollIndicator={false}
          contentInsetAdjustmentBehavior="automatic"
          bounces={true}
          scrollEventThrottle={16}
        >
          {/* Header */}
          <View style={styles.header} accessible={true} accessibilityRole="header">
            <Image 
              source={require('../../../assets/Jharkhand_Rajakiya_Chihna.png')} 
              style={styles.logo}
              resizeMode="contain"
              accessible={true}
              accessibilityLabel="Government of Jharkhand official emblem"
            />
            <Text 
              style={[
                styles.title,
                {
                  fontSize: isTablet ? 28 : isSmallPhone ? 20 : 24,
                }
              ]}
              accessible={true}
              accessibilityRole="header"
              accessibilityLabel="Civic Issue Reporter - Main application title"
            >
              Civic Issue Reporter
            </Text>
            <Text 
              style={[
                styles.subtitle,
                {
                  fontSize: isTablet ? 18 : isSmallPhone ? 14 : 16,
                }
              ]}
              accessible={true}
              accessibilityLabel="Government of Jharkhand"
            >
              Government of Jharkhand
            </Text>
          </View>

          {/* Registration Form */}
          <View style={[
            styles.form,
            {
              maxWidth: isTablet ? 400 : '100%',
              alignSelf: 'center',
              padding: isSmallPhone ? 15 : 20,
              marginHorizontal: isTablet ? 0 : 5,
              marginBottom: 0,
            }
          ]}>

            <Text 
              style={[
                styles.formTitle,
                {
                  fontSize: isTablet ? 22 : isSmallPhone ? 18 : 20,
                }
              ]}
              accessible={true}
              accessibilityRole="header"
              accessibilityLabel="Create Account - Registration form"
            >
              Create Account
            </Text>

            {/* Tab Selection */}
            <View style={styles.tabContainer}>
              <TouchableOpacity 
                style={[
                  styles.tabButton,
                  registrationType === 'email' && styles.tabButtonActive
                ]}
                onPress={() => handleTabSelect('email')}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Register with Email"
              >
                <Ionicons 
                  name="mail" 
                  size={20} 
                  color={registrationType === 'email' ? '#fff' : '#666'} 
                />
                <Text style={[
                  styles.tabButtonText,
                  registrationType === 'email' && styles.tabButtonTextActive
                ]}>
                  Email
                </Text>
              </TouchableOpacity>

              <TouchableOpacity 
                style={[
                  styles.tabButton,
                  registrationType === 'phone' && styles.tabButtonActive
                ]}
                onPress={() => handleTabSelect('phone')}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Register with Phone"
              >
                <Ionicons 
                  name="call" 
                  size={20} 
                  color={registrationType === 'phone' ? '#fff' : '#666'} 
                />
                <Text style={[
                  styles.tabButtonText,
                  registrationType === 'phone' && styles.tabButtonTextActive
                ]}>
                  Phone
                </Text>
              </TouchableOpacity>
            </View>
              
            {/* Name Field - Always show */}
            <View style={styles.inputContainer}>
              <Ionicons 
                name="person-outline" 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={t('auth.fullName')}
                value={formData.name}
                onChangeText={(value) => handleInputChange('name', value)}
                autoCapitalize="words"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel="Full name input field"
                accessibilityHint="Enter your full name"
                returnKeyType="next"
              />
            </View>

            {/* Email/Phone Field - Dynamic based on selected tab */}
            <View style={styles.inputContainer}>
              <Ionicons 
                name={registrationType === 'email' ? "mail-outline" : "call-outline"} 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={registrationType === 'email' ? t('auth.email') : t('auth.phoneNumber')}
                value={registrationType === 'email' ? formData.email : formData.phone}
                onChangeText={(value) => handleInputChange(registrationType === 'email' ? 'email' : 'phone', value)}
                keyboardType={registrationType === 'email' ? "email-address" : "phone-pad"}
                autoCapitalize="none"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel={registrationType === 'email' ? "Email address input field" : "Phone number input field"}
                accessibilityHint={registrationType === 'email' ? "Enter your email address" : "Enter your phone number"}
                returnKeyType="next"
              />
            </View>

            {/* Password Fields - Always show */}
            <View style={styles.inputContainer}>
              <Ionicons 
                name="lock-closed-outline" 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={t('auth.password')}
                value={formData.password}
                onChangeText={(value) => handleInputChange('password', value)}
                secureTextEntry={!showPassword}
                autoCapitalize="none"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel="Password input field"
                accessibilityHint="Enter your password"
                returnKeyType="next"
              />
              <TouchableOpacity 
                onPress={() => setShowPassword(!showPassword)}
                style={styles.eyeIcon}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel={showPassword ? "Hide password" : "Show password"}
                accessibilityHint={showPassword ? "Tap to hide your password" : "Tap to show your password"}
              >
                <Ionicons 
                  name={showPassword ? "eye-off-outline" : "eye-outline"} 
                  size={20} 
                  color="#555" 
                />
              </TouchableOpacity>
            </View>

            <View style={styles.inputContainer}>
              <Ionicons 
                name="lock-closed-outline" 
                size={20} 
                color="#555" 
                style={styles.inputIcon}
                accessible={false}
              />
              <TextInput
                style={styles.input}
                placeholder={t('auth.confirmPassword')}
                value={formData.confirmPassword}
                onChangeText={(value) => handleInputChange('confirmPassword', value)}
                secureTextEntry={!showConfirmPassword}
                autoCapitalize="none"
                autoCorrect={false}
                accessible={true}
                accessibilityLabel="Confirm password input field"
                accessibilityHint="Re-enter your password to confirm"
                returnKeyType="done"
              />
              <TouchableOpacity 
                onPress={() => setShowConfirmPassword(!showConfirmPassword)}
                style={styles.eyeIcon}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel={showConfirmPassword ? "Hide confirm password" : "Show confirm password"}
                accessibilityHint={showConfirmPassword ? "Tap to hide your confirm password" : "Tap to show your confirm password"}
              >
                <Ionicons 
                  name={showConfirmPassword ? "eye-off-outline" : "eye-outline"} 
                  size={20} 
                  color="#555" 
                />
              </TouchableOpacity>
            </View>

            {/* Register Button */}
            <TouchableOpacity 
              style={[styles.registerButton, loading && styles.registerButtonDisabled]}
              onPress={registrationType === 'email' ? handleEmailRegister : handlePhoneRegister}
              disabled={loading}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel={loading ? "Creating account, please wait" : `Create account with ${registrationType}`}
              accessibilityHint={`Tap to create your account using ${registrationType}`}
              accessibilityState={{ disabled: loading }}
            >
              <Text style={styles.registerButtonText}>
                {loading ? t('common.loading') : `Create Account with ${registrationType === 'email' ? 'Email' : 'Phone'}`}
              </Text>
            </TouchableOpacity>

            {/* Divider */}
            <View style={styles.divider}>
              <View style={styles.dividerLine} />
              <Text style={styles.dividerText}>OR</Text>
              <View style={styles.dividerLine} />
            </View>

            {/* Google Sign-in Button */}
            <TouchableOpacity 
              style={styles.googleButton}
              onPress={handleGoogleRegister}
              disabled={loading}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel="Sign up with Google"
              accessibilityHint="Tap to create your account using Google"
              accessibilityState={{ disabled: loading }}
            >
              <Ionicons name="logo-google" size={20} color="#DB4437" />
              <Text style={styles.googleButtonText}>Sign up with Google</Text>
            </TouchableOpacity>


            {/* Terms & Conditions */}
            <View 
              style={styles.termsContainer}
              accessible={true}
              accessibilityRole="text"
              accessibilityLabel="By creating an account, you agree to our Terms and Conditions"
            >
              <Text style={styles.termsText}>By creating an account, you agree to our </Text>
              <TouchableOpacity 
                onPress={handleTermsPress}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Terms and Conditions"
                accessibilityHint="Tap to view terms and conditions"
              >
                <Text style={styles.termsLink}>Terms & Conditions</Text>
              </TouchableOpacity>
              <Text style={styles.termsText}>.</Text>
            </View>

            {/* Login Link */}
            <View 
              style={styles.loginContainer}
              accessible={true}
              accessibilityRole="text"
              accessibilityLabel="Already have an account? Sign in here"
            >
              <Text style={styles.loginText}>{t('auth.hasAccount')} </Text>
              <TouchableOpacity 
                onPress={() => navigation.navigate('Login' as never)}
                accessible={true}
                accessibilityRole="button"
                accessibilityLabel="Sign in to existing account"
                accessibilityHint="Tap to go to login screen"
              >
                <Text style={styles.loginLink}>{t('auth.signIn')}</Text>
              </TouchableOpacity>
            </View>
          </View>
        </ScrollView>
      </KeyboardAvoidingView>

      {/* Terms of Service Modal */}
      <Modal
        visible={termsModal}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <SafeAreaView style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <TouchableOpacity onPress={() => setTermsModal(false)}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
            <Text style={styles.modalTitle}>Terms of Service</Text>
            <View style={{ width: 24 }} />
          </View>
          
          <ScrollView style={styles.modalContent}>
            <Text style={styles.policyText}>
              <Text style={styles.policyTitle}>Terms of Service{'\n\n'}</Text>
              
              <Text style={styles.policySubtitle}>Acceptance of Terms{'\n'}</Text>
              By using this application, you agree to comply with these terms of service and all applicable laws and regulations.{'\n\n'}
              
              <Text style={styles.policySubtitle}>Proper Use{'\n'}</Text>
              This application is intended for reporting legitimate civic issues. Misuse, including false reports or spam, may result in account suspension.{'\n\n'}
              
              <Text style={styles.policySubtitle}>Content Guidelines{'\n'}</Text>
              All submitted content must be respectful, accurate, and relevant to civic issues. Inappropriate content will be removed.{'\n\n'}
              
              <Text style={styles.policySubtitle}>Government Services{'\n'}</Text>
              This app facilitates communication with government services but does not guarantee specific response times or outcomes.{'\n\n'}
              
              <Text style={styles.policySubtitle}>Modifications{'\n'}</Text>
              We reserve the right to modify these terms at any time. Continued use constitutes acceptance of updated terms.
            </Text>
          </ScrollView>
        </SafeAreaView>
      </Modal>
    </SafeAreaView>
  );
};

