import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { RootState } from '../../store/store';
import WorkerHeader from '../../components/WorkerHeader';

const { width, height } = Dimensions.get('window');

interface MapIssue {
  id: string;
  issueId: string;
  title: string;
  location: string;
  latitude: number;
  longitude: number;
  status: 'assigned' | 'in_progress' | 'completed';
  priority: 'low' | 'medium' | 'high';
  category: string;
}

const WorkerMapScreen = () => {
  const { user, token } = useSelector((state: RootState) => state.auth);
  const [mapIssues, setMapIssues] = useState<MapIssue[]>([]);
  const [selectedIssue, setSelectedIssue] = useState<MapIssue | null>(null);
  const [loading, setLoading] = useState(false);
  const [mapView, setMapView] = useState<'list' | 'map'>('list');

  // Mock location data for demo (in real app, use react-native-maps)
  const mockIssues: MapIssue[] = [
    {
      id: 'assign-001',
      issueId: 'issue-001',
      title: 'Pothole on Main Street',
      location: 'Main Street, Ranchi',
      latitude: 23.3441,
      longitude: 85.3096,
      status: 'assigned',
      priority: 'high',
      category: 'Potholes'
    },
    {
      id: 'assign-002',
      issueId: 'issue-002',
      title: 'Street Light Not Working',
      location: 'Park Road, Ranchi',
      latitude: 23.3629,
      longitude: 85.3371,
      status: 'in_progress',
      priority: 'medium',
      category: 'Street Lights'
    },
    {
      id: 'assign-003',
      issueId: 'issue-003',
      title: 'Garbage Collection Required',
      location: 'Market Area, Ranchi',
      latitude: 23.3525,
      longitude: 85.3233,
      status: 'completed',
      priority: 'low',
      category: 'Garbage'
    }
  ];

  useEffect(() => {
    fetchMapIssues();
  }, []);

  const fetchMapIssues = async () => {
    setLoading(true);
    try {
      // For demo, using mock data
      // In real app: fetch from API
      setMapIssues(mockIssues);
    } catch (error) {
      console.error('Error fetching map issues:', error);
      Alert.alert('Error', 'Failed to load map data');
    } finally {
      setLoading(false);
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#f44336';
      case 'medium': return '#FF9800';
      case 'low': return '#4CAF50';
      default: return '#666';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'assigned': return '#2196F3';
      case 'in_progress': return '#FF9800';
      case 'completed': return '#4CAF50';
      default: return '#666';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'assigned': return 'clipboard';
      case 'in_progress': return 'sync';
      case 'completed': return 'checkmark-circle';
      default: return 'help-circle';
    }
  };

  const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    // Haversine formula for distance calculation
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const navigateToIssue = (issue: MapIssue) => {
    // In real app, open navigation app
    Alert.alert(
      'Navigate to Issue',
      `Open navigation to ${issue.location}?`,
      [
        { text: 'Cancel', style: 'cancel' },
        { text: 'Open Maps', onPress: () => {
          // Open external maps app
          console.log(`Navigate to: ${issue.latitude}, ${issue.longitude}`);
        }}
      ]
    );
  };

  const renderMapView = () => (
    <View style={styles.mapContainer}>
      <View style={styles.mapPlaceholder}>
        <Ionicons name="map" size={64} color="#ccc" />
        <Text style={styles.mapPlaceholderText}>Map View</Text>
        <Text style={styles.mapPlaceholderSubtext}>
          Interactive map would be displayed here
        </Text>
        <Text style={styles.mapPlaceholderNote}>
          (Requires react-native-maps integration)
        </Text>
      </View>

      {/* Map Controls */}
      <View style={styles.mapControls}>
        <TouchableOpacity style={styles.mapControlButton}>
          <Ionicons name="locate" size={24} color="#FF6B35" />
        </TouchableOpacity>
        <TouchableOpacity style={styles.mapControlButton}>
          <Ionicons name="layers" size={24} color="#FF6B35" />
        </TouchableOpacity>
      </View>

      {/* Issue Markers Info */}
      <View style={styles.mapLegend}>
        <View style={styles.legendItem}>
          <View style={[styles.legendMarker, { backgroundColor: '#f44336' }]} />
          <Text style={styles.legendText}>High Priority</Text>
        </View>
        <View style={styles.legendItem}>
          <View style={[styles.legendMarker, { backgroundColor: '#FF9800' }]} />
          <Text style={styles.legendText}>Medium Priority</Text>
        </View>
        <View style={styles.legendItem}>
          <View style={[styles.legendMarker, { backgroundColor: '#4CAF50' }]} />
          <Text style={styles.legendText}>Low Priority</Text>
        </View>
      </View>
    </View>
  );

  const renderListView = () => (
    <View style={styles.listContainer}>
      {mapIssues.map((issue) => (
        <TouchableOpacity
          key={issue.id}
          style={styles.issueCard}
          onPress={() => setSelectedIssue(issue)}
        >
          <View style={styles.cardHeader}>
            <View style={styles.statusBadge}>
              <Ionicons 
                name={getStatusIcon(issue.status) as any} 
                size={14} 
                color={getStatusColor(issue.status)} 
              />
              <Text style={[styles.statusText, { color: getStatusColor(issue.status) }]}>
                {issue.status.replace('_', ' ').toUpperCase()}
              </Text>
            </View>
            <View style={[styles.priorityBadge, { backgroundColor: `${getPriorityColor(issue.priority)}20` }]}>
              <Text style={[styles.priorityText, { color: getPriorityColor(issue.priority) }]}>
                {issue.priority.toUpperCase()}
              </Text>
            </View>
          </View>

          <Text style={styles.issueTitle}>{issue.title}</Text>

          <View style={styles.locationInfo}>
            <Ionicons name="location" size={16} color="#666" />
            <Text style={styles.locationText}>{issue.location}</Text>
          </View>

          <View style={styles.distanceInfo}>
            <Ionicons name="navigate" size={14} color="#666" />
            <Text style={styles.distanceText}>
              {calculateDistance(23.3441, 85.3096, issue.latitude, issue.longitude).toFixed(1)} km away
            </Text>
          </View>

          <View style={styles.cardActions}>
            <TouchableOpacity 
              style={styles.navigateButton}
              onPress={() => navigateToIssue(issue)}
            >
              <Ionicons name="navigate" size={16} color="white" />
              <Text style={styles.navigateButtonText}>Navigate</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.detailsButton}>
              <Text style={styles.detailsButtonText}>View Details</Text>
            </TouchableOpacity>
          </View>
        </TouchableOpacity>
      ))}
    </View>
  );

  const insets = useSafeAreaInsets();

  const headerRightComponent = (
    <View style={styles.headerActions}>
      <TouchableOpacity
        style={[styles.viewToggle, mapView === 'list' && styles.activeViewToggle]}
        onPress={() => setMapView('list')}
      >
        <Ionicons name="list" size={18} color={mapView === 'list' ? 'white' : 'rgba(255,255,255,0.7)'} />
      </TouchableOpacity>
      <TouchableOpacity
        style={[styles.viewToggle, mapView === 'map' && styles.activeViewToggle]}
        onPress={() => setMapView('map')}
      >
        <Ionicons name="map" size={18} color={mapView === 'map' ? 'white' : 'rgba(255,255,255,0.7)'} />
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <WorkerHeader
        title="Issue Locations"
        subtitle={`${mapIssues.length} locations`}
        rightComponent={headerRightComponent}
      />

      {/* Stats Bar */}
      <View style={styles.statsBar}>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{mapIssues.filter(i => i.status === 'assigned').length}</Text>
          <Text style={styles.statLabel}>Assigned</Text>
        </View>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{mapIssues.filter(i => i.status === 'in_progress').length}</Text>
          <Text style={styles.statLabel}>In Progress</Text>
        </View>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{mapIssues.filter(i => i.status === 'completed').length}</Text>
          <Text style={styles.statLabel}>Completed</Text>
        </View>
      </View>

      {/* Content */}
      {mapView === 'map' ? renderMapView() : renderListView()}

      {/* Selected Issue Modal */}
      {selectedIssue && (
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <View style={styles.modalHeader}>
              <Text style={styles.modalTitle}>{selectedIssue.title}</Text>
              <TouchableOpacity onPress={() => setSelectedIssue(null)}>
                <Ionicons name="close" size={24} color="#666" />
              </TouchableOpacity>
            </View>

            <View style={styles.modalBody}>
              <View style={styles.modalRow}>
                <Ionicons name="location" size={16} color="#666" />
                <Text style={styles.modalText}>{selectedIssue.location}</Text>
              </View>
              <View style={styles.modalRow}>
                <Ionicons name="folder" size={16} color="#666" />
                <Text style={styles.modalText}>{selectedIssue.category}</Text>
              </View>
              <View style={styles.modalRow}>
                <Ionicons name="flag" size={16} color={getPriorityColor(selectedIssue.priority)} />
                <Text style={styles.modalText}>{selectedIssue.priority} Priority</Text>
              </View>
            </View>

            <View style={styles.modalActions}>
              <TouchableOpacity 
                style={styles.modalNavigateButton}
                onPress={() => {
                  navigateToIssue(selectedIssue);
                  setSelectedIssue(null);
                }}
              >
                <Ionicons name="navigate" size={18} color="white" />
                <Text style={styles.modalNavigateText}>Navigate</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={styles.modalDetailsButton}
                onPress={() => setSelectedIssue(null)}
              >
                <Text style={styles.modalDetailsText}>View Full Details</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      )}
    </View>
  );
};

