import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
  RefreshControl,
  Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { RootState } from '../../store/store';
import WorkerHeader from '../../components/WorkerHeader';

interface WorkHistoryItem {
  id: string;
  issueId: string;
  title: string;
  description: string;
  category: string;
  status: 'completed' | 'in_progress' | 'assigned';
  priority: 'low' | 'medium' | 'high' | 'critical';
  location: {
    address: string;
    latitude: number;
    longitude: number;
  };
  assignedDate: string;
  completedDate?: string;
  rating?: number;
  feedback?: string;
  beforePhotos?: string[];
  afterPhotos?: string[];
  timeSpent?: number; // in hours
}

interface WorkHistoryScreenProps {
  navigation: any;
}

const WorkHistoryScreen: React.FC<WorkHistoryScreenProps> = ({ navigation }) => {
  const { user, token } = useSelector((state: RootState) => state.auth);
  const insets = useSafeAreaInsets();

  const [workHistory, setWorkHistory] = useState<WorkHistoryItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [filter, setFilter] = useState<'all' | 'completed' | 'in_progress'>('all');

  // Mock data for demonstration
  const mockWorkHistory: WorkHistoryItem[] = [
    {
      id: 'work-001',
      issueId: 'issue-001',
      title: 'Pothole Repair on Main Street',
      description: 'Large pothole causing traffic issues',
      category: 'Road Maintenance',
      status: 'completed',
      priority: 'high',
      location: {
        address: 'Main Street, Ranchi',
        latitude: 23.3441,
        longitude: 85.3096,
      },
      assignedDate: '2024-01-15T09:00:00Z',
      completedDate: '2024-01-15T14:30:00Z',
      rating: 4.8,
      feedback: 'Excellent work, completed quickly and efficiently',
      timeSpent: 5.5,
      beforePhotos: ['photo1.jpg'],
      afterPhotos: ['photo2.jpg'],
    },
    {
      id: 'work-002',
      issueId: 'issue-002',
      title: 'Street Light Installation',
      description: 'Install new LED street light',
      category: 'Electrical',
      status: 'completed',
      priority: 'medium',
      location: {
        address: 'Park Road, Ranchi',
        latitude: 23.3629,
        longitude: 85.3371,
      },
      assignedDate: '2024-01-14T08:00:00Z',
      completedDate: '2024-01-14T16:00:00Z',
      rating: 4.5,
      feedback: 'Good work, light is working properly',
      timeSpent: 8,
    },
    {
      id: 'work-003',
      issueId: 'issue-003',
      title: 'Garbage Collection',
      description: 'Clear accumulated garbage from market area',
      category: 'Sanitation',
      status: 'in_progress',
      priority: 'low',
      location: {
        address: 'Market Area, Ranchi',
        latitude: 23.3525,
        longitude: 85.3233,
      },
      assignedDate: '2024-01-16T07:00:00Z',
      timeSpent: 2,
    },
  ];

  useEffect(() => {
    fetchWorkHistory();
  }, []);

  const fetchWorkHistory = async () => {
    if (!user?.id) return;

    setLoading(true);
    try {
      console.log('Fetching work history for user:', user.id);
      const response = await fetch(`http://192.168.29.36:3003/api/v1/workers/${user.id}/work-history`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();
      console.log('Work history API response:', data);

      if (data.success) {
        setWorkHistory(data.data.workHistory);
      } else {
        console.error('Work history API error:', data.error);
        Alert.alert('Error', data.error?.message || 'Failed to load work history');
      }
    } catch (error) {
      console.error('Error fetching work history:', error);
      Alert.alert('Error', 'Failed to load work history');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await fetchWorkHistory();
    setRefreshing(false);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return '#4CAF50';
      case 'in_progress': return '#FF9800';
      case 'assigned': return '#2196F3';
      default: return '#666';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return '#f44336';
      case 'high': return '#FF5722';
      case 'medium': return '#FF9800';
      case 'low': return '#4CAF50';
      default: return '#666';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const calculateDuration = (startDate: string, endDate?: string) => {
    const start = new Date(startDate);
    const end = endDate ? new Date(endDate) : new Date();
    const diffHours = Math.abs(end.getTime() - start.getTime()) / (1000 * 60 * 60);
    return diffHours.toFixed(1);
  };

  const filteredHistory = workHistory.filter(item => {
    if (filter === 'all') return true;
    return item.status === filter;
  });

  const renderFilterButtons = () => (
    <View style={styles.filterContainer}>
      <TouchableOpacity
        style={[styles.filterButton, filter === 'all' && styles.filterButtonActive]}
        onPress={() => setFilter('all')}
      >
        <Text style={[styles.filterText, filter === 'all' && styles.filterTextActive]}>
          All ({workHistory.length})
        </Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={[styles.filterButton, filter === 'completed' && styles.filterButtonActive]}
        onPress={() => setFilter('completed')}
      >
        <Text style={[styles.filterText, filter === 'completed' && styles.filterTextActive]}>
          Completed ({workHistory.filter(item => item.status === 'completed').length})
        </Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={[styles.filterButton, filter === 'in_progress' && styles.filterButtonActive]}
        onPress={() => setFilter('in_progress')}
      >
        <Text style={[styles.filterText, filter === 'in_progress' && styles.filterTextActive]}>
          In Progress ({workHistory.filter(item => item.status === 'in_progress').length})
        </Text>
      </TouchableOpacity>
    </View>
  );

  const renderWorkItem = (item: WorkHistoryItem) => (
    <TouchableOpacity key={item.id} style={styles.workItem}>
      <View style={styles.workHeader}>
        <View style={styles.workTitleRow}>
          <Text style={styles.workTitle} numberOfLines={1}>{item.title}</Text>
          <View style={[styles.statusBadge, { backgroundColor: getStatusColor(item.status) }]}>
            <Text style={styles.statusText}>
              {item.status.replace('_', ' ').toUpperCase()}
            </Text>
          </View>
        </View>

        <View style={styles.workMetaRow}>
          <View style={styles.categoryContainer}>
            <Ionicons name="folder" size={14} color="#666" />
            <Text style={styles.categoryText}>{item.category}</Text>
          </View>
          <View style={[styles.priorityBadge, { backgroundColor: getPriorityColor(item.priority) }]}>
            <Text style={styles.priorityText}>{item.priority.toUpperCase()}</Text>
          </View>
        </View>
      </View>

      <Text style={styles.workDescription} numberOfLines={2}>{item.description}</Text>

      <View style={styles.locationRow}>
        <Ionicons name="location" size={14} color="#666" />
        <Text style={styles.locationText} numberOfLines={1}>{item.location.address}</Text>
      </View>

      <View style={styles.workFooter}>
        <View style={styles.dateContainer}>
          <Text style={styles.dateLabel}>Assigned:</Text>
          <Text style={styles.dateValue}>{formatDate(item.assignedDate)}</Text>
        </View>

        {item.completedDate && (
          <View style={styles.dateContainer}>
            <Text style={styles.dateLabel}>Completed:</Text>
            <Text style={styles.dateValue}>{formatDate(item.completedDate)}</Text>
          </View>
        )}
      </View>

      {item.timeSpent && (
        <View style={styles.timeSpentRow}>
          <Ionicons name="time" size={14} color="#666" />
          <Text style={styles.timeSpentText}>Time spent: {item.timeSpent}h</Text>
        </View>
      )}

      {item.rating && (
        <View style={styles.ratingRow}>
          <Ionicons name="star" size={14} color="#FFD700" />
          <Text style={styles.ratingText}>Rating: {item.rating}/5</Text>
          {item.feedback && (
            <Text style={styles.feedbackText} numberOfLines={1}>â€¢ {item.feedback}</Text>
          )}
        </View>
      )}
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <WorkerHeader
        title="Work History"
        showBackButton={true}
        onBackPress={() => navigation.goBack()}
      />

      <ScrollView
        style={styles.scrollView}
        contentContainerStyle={[styles.scrollContent, { paddingBottom: insets.bottom + 20 }]}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        showsVerticalScrollIndicator={false}
      >
        {renderFilterButtons()}

        {loading ? (
          <View style={styles.loadingContainer}>
            <Text style={styles.loadingText}>Loading work history...</Text>
          </View>
        ) : filteredHistory.length === 0 ? (
          <View style={styles.emptyContainer}>
            <Ionicons name="document-text-outline" size={64} color="#ccc" />
            <Text style={styles.emptyTitle}>No Work History</Text>
            <Text style={styles.emptyText}>
              {filter === 'all' 
                ? 'You haven\'t been assigned any work yet.'
                : `No ${filter.replace('_', ' ')} assignments found.`
              }
            </Text>
          </View>
        ) : (
          <View style={styles.historyList}>
            {filteredHistory.map(renderWorkItem)}
          </View>
        )}
      </ScrollView>
    </View>
  );
};

