import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSelector } from 'react-redux';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { RootState } from '../../store/store';
import WorkerHeader from '../../components/WorkerHeader';

const { width } = Dimensions.get('window');

interface WorkReport {
  id: string;
  issueId: string;
  title: string;
  category: string;
  status: 'completed' | 'in_progress' | 'assigned';
  startedAt: string;
  completedAt?: string;
  duration?: number;
  location: string;
  priority: 'low' | 'medium' | 'high';
  photos: string[];
  notes: string[];
}

interface ReportStats {
  totalCompleted: number;
  totalHours: number;
  avgDuration: number;
  completionRate: number;
  thisWeekCompleted: number;
  thisMonthCompleted: number;
}

const WorkerReportsScreen = () => {
  const { user, token } = useSelector((state: RootState) => state.auth);
  const [reports, setReports] = useState<WorkReport[]>([]);
  const [stats, setStats] = useState<ReportStats>({
    totalCompleted: 0,
    totalHours: 0,
    avgDuration: 0,
    completionRate: 0,
    thisWeekCompleted: 0,
    thisMonthCompleted: 0,
  });
  const [selectedPeriod, setSelectedPeriod] = useState<'week' | 'month' | 'all'>('week');
  const [refreshing, setRefreshing] = useState(false);

  // Mock data for demo
  const mockReports: WorkReport[] = [
    {
      id: 'report-001',
      issueId: 'issue-001',
      title: 'Pothole Repair on Main Street',
      category: 'Potholes',
      status: 'completed',
      startedAt: '2024-01-15T08:00:00Z',
      completedAt: '2024-01-15T11:30:00Z',
      duration: 3.5,
      location: 'Main Street, Ranchi',
      priority: 'high',
      photos: ['before.jpg', 'after.jpg'],
      notes: ['Used asphalt mix', 'Road surface leveled', 'Traffic restored']
    },
    {
      id: 'report-002',
      issueId: 'issue-002',
      title: 'Street Light Replacement',
      category: 'Street Lights',
      status: 'completed',
      startedAt: '2024-01-14T14:00:00Z',
      completedAt: '2024-01-14T16:00:00Z',
      duration: 2,
      location: 'Park Road, Ranchi',
      priority: 'medium',
      photos: ['repair.jpg'],
      notes: ['Replaced LED bulb', 'Checked electrical connection']
    },
    {
      id: 'report-003',
      issueId: 'issue-003',
      title: 'Garbage Collection - Market Area',
      category: 'Garbage',
      status: 'completed',
      startedAt: '2024-01-13T09:00:00Z',
      completedAt: '2024-01-13T11:30:00Z',
      duration: 2.5,
      location: 'Market Area, Ranchi',
      priority: 'low',
      photos: ['before_cleanup.jpg', 'after_cleanup.jpg'],
      notes: ['Collected 15 bags of waste', 'Area sanitized']
    },
    {
      id: 'report-004',
      issueId: 'issue-004',
      title: 'Water Pipe Repair',
      category: 'Water Supply',
      status: 'in_progress',
      startedAt: '2024-01-16T10:00:00Z',
      location: 'Residential Colony, Ranchi',
      priority: 'high',
      photos: ['leak_identified.jpg'],
      notes: ['Located leak source', 'Waiting for replacement parts']
    }
  ];

  useEffect(() => {
    fetchReports();
  }, [selectedPeriod]);

  const fetchReports = async () => {
    setRefreshing(true);
    try {
      // For demo, using mock data
      // In real app: fetch from API based on selectedPeriod
      const filteredReports = filterReportsByPeriod(mockReports);
      setReports(filteredReports);
      calculateStats(filteredReports);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setRefreshing(false);
    }
  };

  const filterReportsByPeriod = (allReports: WorkReport[]) => {
    const now = new Date();
    const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay()));
    const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);

    switch (selectedPeriod) {
      case 'week':
        return allReports.filter(report => 
          new Date(report.startedAt) >= startOfWeek
        );
      case 'month':
        return allReports.filter(report => 
          new Date(report.startedAt) >= startOfMonth
        );
      default:
        return allReports;
    }
  };

  const calculateStats = (reportsList: WorkReport[]) => {
    const completed = reportsList.filter(r => r.status === 'completed');
    const totalHours = completed.reduce((sum, r) => sum + (r.duration || 0), 0);
    const avgDuration = completed.length > 0 ? totalHours / completed.length : 0;

    setStats({
      totalCompleted: completed.length,
      totalHours,
      avgDuration,
      completionRate: reportsList.length > 0 ? (completed.length / reportsList.length) * 100 : 0,
      thisWeekCompleted: completed.filter(r => 
        new Date(r.startedAt) >= new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
      ).length,
      thisMonthCompleted: completed.filter(r => 
        new Date(r.startedAt) >= new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)
      ).length,
    });
  };

  const onRefresh = () => {
    fetchReports();
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDuration = (hours: number) => {
    const h = Math.floor(hours);
    const m = Math.round((hours - h) * 60);
    return `${h}h ${m}m`;
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return '#f44336';
      case 'medium': return '#FF9800';
      case 'low': return '#4CAF50';
      default: return '#666';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return '#4CAF50';
      case 'in_progress': return '#FF9800';
      case 'assigned': return '#2196F3';
      default: return '#666';
    }
  };

  const renderStatsCard = () => (
    <View style={styles.statsCard}>
      <Text style={styles.statsTitle}>Performance Summary</Text>
      <View style={styles.statsGrid}>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{stats.totalCompleted}</Text>
          <Text style={styles.statLabel}>Completed</Text>
        </View>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{stats.totalHours.toFixed(1)}h</Text>
          <Text style={styles.statLabel}>Total Hours</Text>
        </View>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{stats.avgDuration.toFixed(1)}h</Text>
          <Text style={styles.statLabel}>Avg Duration</Text>
        </View>
        <View style={styles.statItem}>
          <Text style={styles.statNumber}>{stats.completionRate.toFixed(0)}%</Text>
          <Text style={styles.statLabel}>Completion Rate</Text>
        </View>
      </View>
    </View>
  );

  const renderReportCard = (report: WorkReport) => (
    <View key={report.id} style={styles.reportCard}>
      <View style={styles.reportHeader}>
        <View style={styles.reportTitleRow}>
          <Text style={styles.reportTitle}>{report.title}</Text>
          <View style={[styles.statusBadge, { backgroundColor: `${getStatusColor(report.status)}20` }]}>
            <Text style={[styles.statusText, { color: getStatusColor(report.status) }]}>
              {report.status.replace('_', ' ').toUpperCase()}
            </Text>
          </View>
        </View>
        <View style={styles.reportMeta}>
          <View style={styles.metaItem}>
            <Ionicons name="folder" size={14} color="#666" />
            <Text style={styles.metaText}>{report.category}</Text>
          </View>
          <View style={[styles.priorityBadge, { backgroundColor: `${getPriorityColor(report.priority)}20` }]}>
            <Text style={[styles.priorityText, { color: getPriorityColor(report.priority) }]}>
              {report.priority.toUpperCase()}
            </Text>
          </View>
        </View>
      </View>

      <View style={styles.reportDetails}>
        <View style={styles.detailRow}>
          <Ionicons name="location" size={14} color="#666" />
          <Text style={styles.detailText}>{report.location}</Text>
        </View>
        <View style={styles.detailRow}>
          <Ionicons name="time" size={14} color="#666" />
          <Text style={styles.detailText}>Started: {formatDate(report.startedAt)}</Text>
        </View>
        {report.completedAt && (
          <View style={styles.detailRow}>
            <Ionicons name="checkmark-circle" size={14} color="#4CAF50" />
            <Text style={styles.detailText}>Completed: {formatDate(report.completedAt)}</Text>
          </View>
        )}
        {report.duration && (
          <View style={styles.detailRow}>
            <Ionicons name="hourglass" size={14} color="#666" />
            <Text style={styles.detailText}>Duration: {formatDuration(report.duration)}</Text>
          </View>
        )}
      </View>

      {report.photos.length > 0 && (
        <View style={styles.photosSection}>
          <Text style={styles.photosTitle}>Photos ({report.photos.length})</Text>
          <ScrollView horizontal showsHorizontalScrollIndicator={false}>
            {report.photos.map((photo, index) => (
              <View key={index} style={styles.photoPlaceholder}>
                <Ionicons name="image" size={24} color="#ccc" />
                <Text style={styles.photoName}>{photo}</Text>
              </View>
            ))}
          </ScrollView>
        </View>
      )}

      {report.notes.length > 0 && (
        <View style={styles.notesSection}>
          <Text style={styles.notesTitle}>Work Notes</Text>
          {report.notes.map((note, index) => (
            <View key={index} style={styles.noteItem}>
              <Ionicons name="checkmark" size={12} color="#4CAF50" />
              <Text style={styles.noteText}>{note}</Text>
            </View>
          ))}
        </View>
      )}

      <TouchableOpacity style={styles.viewDetailsButton}>
        <Text style={styles.viewDetailsText}>View Full Report</Text>
        <Ionicons name="arrow-forward" size={16} color="#FF6B35" />
      </TouchableOpacity>
    </View>
  );

  const insets = useSafeAreaInsets();

  return (
    <View style={styles.container}>
      <WorkerHeader
        title="Work Reports"
        subtitle={`${reports.length} reports`}
        rightComponent={
          <TouchableOpacity style={styles.exportButton}>
            <Ionicons name="download" size={20} color="white" />
            <Text style={styles.exportText}>Export</Text>
          </TouchableOpacity>
        }
      />

      {/* Period Filter */}
      <View style={styles.filterContainer}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {['week', 'month', 'all'].map((period) => (
            <TouchableOpacity
              key={period}
              style={[
                styles.filterButton,
                selectedPeriod === period && styles.activeFilterButton
              ]}
              onPress={() => setSelectedPeriod(period as any)}
            >
              <Text style={[
                styles.filterButtonText,
                selectedPeriod === period && styles.activeFilterButtonText
              ]}>
                {period === 'week' ? 'This Week' : period === 'month' ? 'This Month' : 'All Time'}
              </Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>

      <ScrollView 
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        showsVerticalScrollIndicator={false}
      >
        {/* Stats Card */}
        {renderStatsCard()}

        {/* Reports List */}
        <View style={styles.reportsSection}>
          <Text style={styles.sectionTitle}>Work History</Text>
          {reports.length > 0 ? (
            reports.map(renderReportCard)
          ) : (
            <View style={styles.emptyState}>
              <Ionicons name="document-text-outline" size={64} color="#ccc" />
              <Text style={styles.emptyStateText}>No reports found</Text>
              <Text style={styles.emptyStateSubtext}>
                Complete some work assignments to see reports here
              </Text>
            </View>
          )}
        </View>
      </ScrollView>
    </View>
  );
};

