import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  SafeAreaView,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useRoute } from '@react-navigation/native';

const TrackIssueScreen = () => {
  const route = useRoute();
  const { issueId } = route.params as { issueId: string };

  // Mock issue data
  const [issue, setIssue] = useState({
    id: issueId,
    title: 'Pothole near my house',
    description: 'Large pothole causing vehicle damage on Main Street',
    category: 'Potholes',
    location: {
      address: 'Main Street, Ranchi, Jharkhand',
    },
    status: 'in_progress',
    submittedAt: '2024-01-05T10:00:00Z',
    deadline: '2024-01-25T00:00:00Z',
    assignedTo: 'Public Works Department',
    estimatedCompletion: '2024-01-22T00:00:00Z',
  });

  const timeline = [
    {
      id: '1',
      status: 'submitted',
      title: 'Issue Submitted',
      description: 'Your issue has been successfully submitted to the system',
      timestamp: '2024-01-05T10:00:00Z',
      completed: true,
    },
    {
      id: '2',
      status: 'acknowledged',
      title: 'Issue Acknowledged',
      description: 'Department has acknowledged your issue and assigned it for review',
      timestamp: '2024-01-06T14:30:00Z',
      completed: true,
    },
    {
      id: '3',
      status: 'assigned',
      title: 'Issue Assigned',
      description: 'Issue has been assigned to Public Works Department team',
      timestamp: '2024-01-08T09:15:00Z',
      completed: true,
    },
    {
      id: '4',
      status: 'in_progress',
      title: 'Work in Progress',
      description: 'Repair work has started. Materials have been arranged.',
      timestamp: '2024-01-12T11:20:00Z',
      completed: true,
    },
    {
      id: '5',
      status: 'completed',
      title: 'Issue Resolved',
      description: 'Pothole has been filled and road surface restored',
      timestamp: null,
      completed: false,
      estimated: '2024-01-22T00:00:00Z',
    },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'submitted': return '#FF9800';
      case 'acknowledged': return '#2196F3';
      case 'assigned': return '#9C27B0';
      case 'in_progress': return '#FF5722';
      case 'completed': return '#4CAF50';
      default: return '#757575';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'submitted': return 'document-text';
      case 'acknowledged': return 'checkmark-circle';
      case 'assigned': return 'person';
      case 'in_progress': return 'construct';
      case 'completed': return 'checkmark-done-circle';
      default: return 'help-circle';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'long',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatDateShort = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
    });
  };

  const getDaysRemaining = (deadlineString: string) => {
    const deadline = new Date(deadlineString);
    const now = new Date();
    const diffTime = deadline.getTime() - now.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  const handleSetReminder = () => {
    Alert.alert(
      'Reminder Set',
      'You will be notified when there are updates on this issue.',
      [{ text: 'OK' }]
    );
  };

  const handleReportProblem = () => {
    Alert.alert(
      'Report Problem',
      'Please describe the issue with the current status or progress.',
      [
        { text: 'Cancel', style: 'cancel' },
        { text: 'Report', onPress: () => {} },
      ]
    );
  };

  const daysRemaining = getDaysRemaining(issue.deadline);

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Issue Header */}
        <View style={styles.header}>
          <Text style={styles.issueTitle}>{issue.title}</Text>
          <Text style={styles.issueDescription}>{issue.description}</Text>

          <View style={styles.issueInfo}>
            <View style={styles.infoRow}>
              <Ionicons name="location-outline" size={16} color="#666" />
              <Text style={styles.infoText}>{issue.location.address}</Text>
            </View>
            <View style={styles.infoRow}>
              <Ionicons name="calendar-outline" size={16} color="#666" />
              <Text style={styles.infoText}>
                Submitted: {formatDateShort(issue.submittedAt)}
              </Text>
            </View>
            <View style={styles.infoRow}>
              <Ionicons name="business-outline" size={16} color="#666" />
              <Text style={styles.infoText}>Assigned to: {issue.assignedTo}</Text>
            </View>
          </View>
        </View>

        {/* Deadline Alert */}
        <View style={[
          styles.deadlineCard,
          daysRemaining <= 3 ? styles.deadlineUrgent : styles.deadlineNormal
        ]}>
          <Ionicons 
            name="time-outline" 
            size={20} 
            color={daysRemaining <= 3 ? '#f44336' : '#FF9800'} 
          />
          <View style={styles.deadlineContent}>
            <Text style={styles.deadlineTitle}>
              {daysRemaining > 0 ? `${daysRemaining} days remaining` : 'Deadline passed'}
            </Text>
            <Text style={styles.deadlineText}>
              Target completion: {formatDateShort(issue.deadline)}
            </Text>
          </View>
        </View>

        {/* Timeline */}
        <View style={styles.timelineSection}>
          <Text style={styles.sectionTitle}>Progress Timeline</Text>

          {timeline.map((item, index) => (
            <View key={item.id} style={styles.timelineItem}>
              <View style={styles.timelineLeft}>
                <View style={[
                  styles.timelineIcon,
                  { backgroundColor: item.completed ? getStatusColor(item.status) : '#f0f0f0' }
                ]}>
                  <Ionicons
                    name={getStatusIcon(item.status) as any}
                    size={20}
                    color={item.completed ? 'white' : '#ccc'}
                  />
                </View>
                {index < timeline.length - 1 && (
                  <View style={[
                    styles.timelineLine,
                    { backgroundColor: item.completed ? getStatusColor(item.status) : '#f0f0f0' }
                  ]} />
                )}
              </View>

              <View style={styles.timelineContent}>
                <View style={styles.timelineHeader}>
                  <Text style={[
                    styles.timelineTitle,
                    { color: item.completed ? '#333' : '#999' }
                  ]}>
                    {item.title}
                  </Text>
                  <Text style={styles.timelineDate}>
                    {item.timestamp ? formatDate(item.timestamp) : 
                     item.estimated ? `Est. ${formatDateShort(item.estimated)}` : 'Pending'}
                  </Text>
                </View>
                <Text style={[
                  styles.timelineDescription,
                  { color: item.completed ? '#666' : '#999' }
                ]}>
                  {item.description}
                </Text>
              </View>
            </View>
          ))}
        </View>

        {/* Action Buttons */}
        <View style={styles.actionSection}>
          <TouchableOpacity style={styles.actionButton} onPress={handleSetReminder}>
            <Ionicons name="notifications-outline" size={20} color="#2E7D32" />
            <Text style={styles.actionButtonText}>Set Reminder</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.actionButton} onPress={handleReportProblem}>
            <Ionicons name="flag-outline" size={20} color="#f44336" />
            <Text style={[styles.actionButtonText, { color: '#f44336' }]}>
              Report Problem
            </Text>
          </TouchableOpacity>
        </View>

        {/* Additional Info */}
        <View style={styles.infoSection}>
          <Text style={styles.sectionTitle}>Additional Information</Text>

          <View style={styles.infoCard}>
            <View style={styles.infoCardHeader}>
              <Ionicons name="information-circle-outline" size={20} color="#2196F3" />
              <Text style={styles.infoCardTitle}>Issue ID</Text>
            </View>
            <Text style={styles.infoCardText}>{issue.id}</Text>
          </View>

          <View style={styles.infoCard}>
            <View style={styles.infoCardHeader}>
              <Ionicons name="pricetag-outline" size={20} color="#2196F3" />
              <Text style={styles.infoCardTitle}>Category</Text>
            </View>
            <Text style={styles.infoCardText}>{issue.category}</Text>
          </View>

          <View style={styles.infoCard}>
            <View style={styles.infoCardHeader}>
              <Ionicons name="calendar-outline" size={20} color="#2196F3" />
              <Text style={styles.infoCardTitle}>Estimated Completion</Text>
            </View>
            <Text style={styles.infoCardText}>
              {formatDateShort(issue.estimatedCompletion)}
            </Text>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

